import axios from "axios";
import React, { useState, useEffect } from "react";

const AddUserModal = ({ isOpen, onClose, onSave, userIdToEdit }) => {
  const [newUser, setNewUser] = useState({
    fullName: '',
    age: '',
    gender: '',
    address: '',
    contactNumber: '',
    email: '',
    password: '',
    userType: ''
  });

  const [message, setMessage] = useState('');

  const fetchUserData = async (id) => {
    try {
      const response = await axios.get(`http://localhost:3001/api/user/${id}`);
      if (response.data) {
        setNewUser({
          fullName: response.data.fullName,
          age: response.data.age,
          gender: response.data.gender,
          address: response.data.address,
          contactNumber: response.data.contactNumber,
          email: response.data.email,
          password: response.data.password,
          userType: response.data.userType
        });
      } else {
        setMessage('No user found');
      }
    } catch (error) {
      setMessage('Error fetching user data');
    }
  };

  useEffect(() => {
    if (isOpen) {
      if (userIdToEdit) {
        fetchUserData(userIdToEdit);
      } else {
        resetForm();
      }
    } else {
      resetForm();
    }
  }, [isOpen, userIdToEdit]);

  const resetForm = () => {
    setNewUser({
      fullName: '',
      age: '',
      gender: '',
      address: '',
      contactNumber: '',
      email: '',
      password: '',
      userType: ''
    });
    setMessage('');
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewUser({ ...newUser, [name]: value });
  };

  const handleSave = async () => {
    if (!newUser.fullName || !newUser.email || !newUser.password) {
      setMessage("Please fill in all fields");
      return;
    }

    try {
      await axios.post("http://localhost:3001/api/user/", newUser);
      onSave(newUser);
      onClose();
    } catch (error) {
      setMessage("Error saving user");
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center">
      <div className="fixed inset-0 bg-black opacity-50" onClick={onClose}></div>
      <div className="bg-white rounded-lg shadow-lg p-6 w-1/2 relative z-10">
        <h2 className="text-2xl font-bold mb-4">Add / Edit User</h2>
        <div className="mb-4">
          <label className="block text-sm font-medium">Full Name</label>
          <input
            type="text"
            name="fullName"
            value={newUser.fullName}
            onChange={handleInputChange}
            className="px-4 py-2 border border-gray-300 rounded-md w-full"
          />
        </div>
        <div className="mb-4">
          <label className="block text-sm font-medium">Age</label>
          <input
            type="number"
            name="age"
            value={newUser.age}
            onChange={handleInputChange}
            className="px-4 py-2 border border-gray-300 rounded-md w-full"
          />
        </div>
        <div className="mb-4">
          <label className="block text-sm font-medium">Gender</label>
          <select
            name="gender"
            value={newUser.gender}
            onChange={handleInputChange}
            className="px-4 py-2 border border-gray-300 rounded-md w-full"
          >
            <option value="">Select Gender</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
          </select>
        </div>
        <div className="mb-4">
          <label className="block text-sm font-medium">Address</label>
          <input
            type="text"
            name="address"
            value={newUser.address}
            onChange={handleInputChange}
            className="px-4 py-2 border border-gray-300 rounded-md w-full"
          />
        </div>
        <div className="mb-4">
          <label className="block text-sm font-medium">Contact Number</label>
          <input
            type="text"
            name="contactNumber"
            value={newUser.contactNumber}
            onChange={handleInputChange}
            className="px-4 py-2 border border-gray-300 rounded-md w-full"
          />
        </div>
        <div className="mb-4">
          <label className="block text-sm font-medium">Email</label>
          <input
            type="email"
            name="email"
            value={newUser.email}
            onChange={handleInputChange}
            className="px-4 py-2 border border-gray-300 rounded-md w-full"
          />
        </div>
        <div className="mb-4">
          <label className="block text-sm font-medium">Password</label>
          <input
            type="password"
            name="password"
            value={newUser.password}
            onChange={handleInputChange}
            className="px-4 py-2 border border-gray-300 rounded-md w-full"
          />
        </div>
        <div className="mb-4">
          <label className="block text-sm font-medium">User Type</label>
          <select
            name="userType"
            value={newUser.userType}
            onChange={handleInputChange}
            className="px-4 py-2 border border-gray-300 rounded-md w-full"
          >
            <option value="">Select User Type</option>
            <option value="Admin">Admin</option>
            <option value="Member">Member</option>
          </select>
        </div>

        {message && <div className="text-red-500 text-sm mb-4">{message}</div>}

        <div className="flex justify-between">
          <button
            onClick={handleSave}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            Save
          </button>
          <button
            onClick={onClose}
            className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-700"
          >
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default AddUserModal;
