import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MemberModal = ({ isOpen, onClose }) => {
  const [memberName, setMemberName] = useState('');
  const [members, setMembers] = useState([]);
  const [newMember, setNewMember] = useState({
    memberId: '',
    fullName: '',
    age: '',
    contactNumber: '',
    gender: '',
    address: '',
    sharedCapital: '',
    email: '',
    password: ''
  });
  const [message, setMessage] = useState('');

  // Function to fetch member data from the API
  const fetchMembers = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/member-information');
      setMembers(response.data);
    } catch (error) {
      console.error('Error fetching members:', error);
      setMessage('Error fetching member data');
    }
  };

  // Call fetchMembers when the modal is opened
  useEffect(() => {
    if (isOpen) {
      fetchMembers();
    }
  }, [isOpen]);

  // Function to handle adding a new member
  const handleAddMember = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3001/api/member-information', newMember);
      setMessage(response.data.message);
      setNewMember({
        memberId: '',
        fullName: '',
        age: '',
        contactNumber: '',
        gender: '',
        address: '',
        sharedCapital: '',
        email: '',
        password: ''
      });
      // Fetch updated member data after adding a new member
      fetchMembers();
    } catch (error) {
      setMessage('Error adding member');
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="fixed inset-0 bg-black opacity-50"></div>
      <div className="bg-white rounded-lg shadow-lg p-6 w-11/12 md:w-1/2 lg:w-1/3 relative z-10">
        <div className="flex justify-end mb-2">
          <button
            onClick={onClose}
            className="text-red-500 text-2xl font-bold"
          >
            &times;
          </button>
        </div>
        <h1 className="text-2xl font-bold text-gray-800 mb-6">Member Management</h1>

        {message && <p className="text-red-500">{message}</p>}
        {members.length > 0 && (
          <div className="mt-4">
            <h3 className="text-lg font-semibold">Member Details</h3>
            <ul className="list-disc list-inside">
              {members.map((member) => (
                <li key={member.id}>
                  {member.fullName} - {member.contactNumber}
                </li>
              ))}
            </ul>
          </div>
        )}

        <h2 className="text-lg font-semibold mt-6 mb-2">Add New Member</h2>
        <form onSubmit={handleAddMember}>
          <div className="grid grid-cols-2 gap-x-8 gap-y-4">
            {Object.keys(newMember).map((key) => (
              <div key={key}>
                <label className="block text-gray-700">
                  {key.charAt(0).toUpperCase() + key.slice(1)}:
                </label>
                <input
                  type="text"
                  value={newMember[key]}
                  onChange={(e) => setNewMember({ ...newMember, [key]: e.target.value })}
                  required
                  className="mt-1 p-2 border border-gray-300 rounded-md w-full"
                />
              </div>
            ))}
          </div>
          <button
            type="submit"
            className="mt-4 bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600"
          >
            Add Member
          </button>
        </form>
      </div>
    </div>
  );
};

export default MemberModal;
