import React, { useState } from "react";
import axios from "axios";

const Announcement = () => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [targetAudience, setTargetAudience] = useState("");
  const [status, setStatus] = useState("New");
  const [announcementId, setAnnouncementId] = useState(""); // For editing existing announcements

  // Add New Announcement
  const handleAdd = async () => {
    try {
      const response = await axios.post("/api/announcements", {
        title,
        content,
        targetAudience,
        status,
      });
      alert(response.data.message || "Announcement added successfully!");
      resetForm();
    } catch (error) {
      console.error("Error adding announcement:", error);
      alert("Failed to add announcement.");
    }
  };

  // Edit Existing Announcement
  const handleEdit = async () => {
    try {
      const response = await axios.put(`/api/announcements/${announcementId}`, {
        title,
        content,
        targetAudience,
        status,
      });
      alert(response.data.message || "Announcement updated successfully!");
      resetForm();
    } catch (error) {
      console.error("Error editing announcement:", error);
      alert("Failed to edit announcement.");
    }
  };

  // Reset Form Fields
  const resetForm = () => {
    setTitle("");
    setContent("");
    setTargetAudience("");
    setStatus("New");
    setAnnouncementId("");
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Announcement</h1>

      <div className="p-6 max-w-2xl">
        <h1 className="text-2xl font-semibold text-gray-800 mb-6">
          {announcementId ? "Edit Announcement" : "Add New Announcement"}
        </h1>

        {/* Announcement ID for editing */}
        {announcementId && (
          <div className="mb-4">
            <label className="block text-gray-700 mb-2">Announcement ID</label>
            <input
              type="text"
              value={announcementId}
              onChange={(e) => setAnnouncementId(e.target.value)}
              className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Enter Announcement ID for editing"
            />
          </div>
        )}

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Content</label>
          <textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
            rows="4"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          ></textarea>
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Target Audience</label>
          <input
            type="text"
            value={targetAudience}
            onChange={(e) => setTargetAudience(e.target.value)}
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="e.g., All Users, Premium Subscribers"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Status</label>
          <select
            value={status}
            onChange={(e) => setStatus(e.target.value)}
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <option value="New">New</option>
            <option value="Urgent">Urgent</option>
            <option value="Update">Update</option>
            <option value="Maintenance">Maintenance</option>
          </select>
        </div>

        <div className="flex space-x-4">
          {/* Add or Edit Button */}
          <button
            onClick={announcementId ? handleEdit : handleAdd}
            className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600"
          >
            {announcementId ? "Edit Announcement" : "Add Announcement"}
          </button>

          {/* Reset Button */}
          <button
            onClick={resetForm}
            className="bg-gray-500 text-white px-4 py-2 rounded-lg hover:bg-gray-600"
          >
            Reset
          </button>
        </div>
      </div>
    </div>
  );
};

export default Announcement;
