import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const AddMemberModal = ({ isOpen, onClose, onSave, memberIdToEdit }) => {
  const [newMember, setNewMember] = useState({
    registrationType: '',
    memberComposition: '',
    dateOfRegistration: '',
    sharedCapital: '',
    fullName: '',
    lastName: '',
    firstName: '',
    middleName: '',
    maidenName: '',
    tin: '',
    dateOfBirth: '',
    birthPlace: '',
    age: '',
    sex: '',
    civilStatus: '',
    highestEducationalAttainment: '',
    occupation: '',
    sourceOfIncome: '',
    nameOfSpouse: '',
    spouseOccupation: '',
    primaryBeneficiary: '',
    primaryRelationship: '',
    primaryContact: '',
    secondaryBeneficiary: '',
    secondaryRelationship: '',
    secondaryContact: '',
    contactNumber: '',
    address: '',
    barangay: '',
    city: '',
    reference1Name: '',
    reference1Position: '',
    reference1Contact: '',
    reference2Name: '',
    reference2Position: '',
    reference2Contact: '',
    reference3Name: '',
    reference3Position: '',
    reference3Contact: '',
    idPicture: null,
    barangayClearance: null,
    tinFile: null,
  });

  const [message, setMessage] = useState('');
  const { id } = useParams();

  const fetchMemberData = async (id) => {
    try {
      const response = await axios.get(`http://localhost:3001/api/members/${id}`);
      if (response.data) {
        setNewMember(response.data); // Populate the form with fetched data
      } else {
        setMessage('No member data found.');
      }
    } catch (error) {
      console.error("Error fetching member data:", error);
      setMessage('Error fetching member data. Please try again later.');
    }
  };

  useEffect(() => {
    if (isOpen) {
      if (memberIdToEdit) {
        fetchMemberData(memberIdToEdit); // Fetch member data when editing
      } else {
        resetForm(); // Reset form when opening modal for a new member
      }
    } else {
      resetForm(); // Reset form when closing the modal
    }
  }, [isOpen, memberIdToEdit]);

  const resetForm = () => {
    setNewMember({
      registrationType: '',
      memberComposition: '',
      dateOfRegistration: '',
      sharedCapital: '',
      fullName: '',
      lastName: '',
      firstName: '',
      middleName: '',
      maidenName: '',
      tin: '',
      dateOfBirth: '',
      birthPlace: '',
      age: '',
      sex: '',
      civilStatus: '',
      highestEducationalAttainment: '',
      occupation: '',
      sourceOfIncome: '',
      nameOfSpouse: '',
      spouseOccupation: '',
      primaryBeneficiary: '',
      primaryRelationship: '',
      primaryContact: '',
      secondaryBeneficiary: '',
      secondaryRelationship: '',
      secondaryContact: '',
      contactNumber: '',
      address: '',
      barangay: '',
      city: '',
      reference1Name: '',
      reference1Position: '',
      reference1Contact: '',
      reference2Name: '',
      reference2Position: '',
      reference2Contact: '',
      reference3Name: '',
      reference3Position: '',
      reference3Contact: '',
      idPicture: null,
      barangayClearance: null,
      tinFile: null,
    });
    setMessage('');
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setNewMember({
      ...newMember,
      [name]: files[0], // Update state with the selected file
    });
  };

  const handleAddOrEditMember = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    Object.keys(newMember).forEach((key) => {
      if (newMember[key]) {
        formData.append(key, newMember[key]); // Add form data
      }
    });

    try {
      const response = await axios[memberIdToEdit ? 'put' : 'post'](
        `http://localhost:3001/api/members${memberIdToEdit ? `/${memberIdToEdit}` : ''}`,
        formData,
        { headers: { 'Content-Type': 'multipart/form-data' } }
      );
      
      setMessage(response.data.message || 'Member data saved successfully!');
      onSave(newMember); // Pass updated member data to parent
      resetForm(); // Reset the form after submission
    } catch (error) {
      console.error("Error adding or updating member:", error);
      if (error.response) {
        setMessage(`Error: ${error.response.data.message || 'An error occurred. Please try again later.'}`);
      } else {
        setMessage('Error adding or updating member. Please try again later.');
      }
    }
  };

  if (!isOpen) return null; // Don't render anything if modal is not open

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="fixed inset-0 bg-black opacity-50" />
      <div className="bg-white rounded-lg shadow-lg p-6 w-11/12 md:w-1/2 lg:w-1/3 relative z-10 max-h-[80vh] overflow-auto">
        <div className="flex justify-end mb-2">
          <button onClick={onClose} className="text-red-500 text-2xl font-bold">&times;</button>
        </div>
        <h1 className="text-2xl font-bold text-gray-800 mb-4">MEMBER MANAGEMENT</h1>
        {message && <p className="text-red-500">{message}</p>}

        {/* Form fields for registration type, personal info, contact info, etc. */}
        {/* Keep the same structure, update the inputs with their respective values */}
        
        <h2 className="text-lg font-semibold mt-4 mb-2">MEMBERâ€™S TYPE REGISTRATION</h2>
        <label className="block text-gray-700">Registration Type:</label>
        <select
          value={newMember.registrationType}
          onChange={(e) => setNewMember({ ...newMember, registrationType: e.target.value })}
          className="mt-1 p-2 border border-gray-300 rounded-md w-full"
        >
          <option value="">Select Type</option>
          <option value="New">New</option>
          <option value="Transfer">Transfer</option>
          <option value="Regular Member">Regular Member</option>
          <option value="Associate Member">Associate Member</option>
        </select>

        {/* Repeat this structure for all other fields such as personal information, contact info, etc. */}

        <div>
          <label className="block text-gray-700">Full Name:</label>
          <div className="grid grid-cols-3 gap-2">
            <input
              type="text"
              value={newMember.lastName}
              onChange={(e) => setNewMember({ ...newMember, lastName: e.target.value })}
              placeholder="Last Name"
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            />
            <input
              type="text"
              value={newMember.firstName}
              onChange={(e) => setNewMember({ ...newMember, firstName: e.target.value })}
              placeholder="First Name"
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            />
            <input
              type="text"
              value={newMember.middleName}
              onChange={(e) => setNewMember({ ...newMember, middleName: e.target.value })}
              placeholder="Middle Name"
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            />
          </div>
        </div>

        {/* Add other form fields similarly */}
        
        <h2 className="text-lg font-semibold mt-4 mb-2">Upload Documents</h2>
        <div>
          <label className="block text-gray-700">ID Picture:</label>
          <input
            type="file"
            name="idPicture"
            onChange={handleFileChange}
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
        </div>
        <div>
          <label className="block text-gray-700">Barangay Clearance:</label>
          <input
            type="file"
            name="barangayClearance"
            onChange={handleFileChange}
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
        </div>
        <div>
          <label className="block text-gray-700">TIN File:</label>
          <input
            type="file"
            name="tinFile"
            onChange={handleFileChange}
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
        </div>

        <div className="mt-4 flex justify-end">
          <button
            onClick={handleAddOrEditMember}
            className="px-4 py-2 bg-blue-500 text-white rounded-md"
          >
            {memberIdToEdit ? 'Update Member' : 'Add Member'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default AddMemberModal;
