import React, { useState, useEffect } from "react";
import axios from "axios";
import { FaPlus, FaTrash, FaEdit, FaSearch } from "react-icons/fa";
import AddUserModal from "../components/modal/AddUserModal"; 
import UserInformationModal from "../components/modal/MemberProfileModal";

const Users = () => {
  const [users, setUsers] = useState([]); // State to store the users
  const [loading, setLoading] = useState(false); // State to handle loading state
  const [error, setError] = useState(null); // State to handle errors
  const [isAddEditModalOpen, setIsAddEditModalOpen] = useState(false);
  const [isViewModalOpen, setIsViewModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [searchTerm, setSearchTerm] = useState(""); // State for search term

  // Fetch users from the API
  const fetchUsers = async () => {
    setLoading(true);
    try {
      const params = searchTerm ? { name: searchTerm } : {}; // Use search term if available
      const response = await axios.get("http://localhost:3001/api/users", { params });
      setUsers(response.data); // Set users to state
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.message || err.message);
    } finally {
      setLoading(false);
    }
  };

  // Fetch a specific user's details by ID
  const fetchUser = async (userId) => {
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:3001/api/users/${userId}`);
      setSelectedUser(response.data); // Set selected user data
      setIsAddEditModalOpen(true); // Open the modal after fetching data
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.message || err.message);
    } finally {
      setLoading(false);
    }
  };

  // Call fetchUsers when the component is mounted or searchTerm changes
  useEffect(() => {
    fetchUsers();
  }, [searchTerm]);

  const openAddEditModal = (user) => {
    if (user) {
      fetchUser(user._id); // Fetch user details if editing an existing user
    } else {
      setSelectedUser(null); // If adding a new user, reset selected user
      setIsAddEditModalOpen(true);
    }
  };

  const closeModals = () => {
    setIsAddEditModalOpen(false);
    setIsViewModalOpen(false);
    setSelectedUser(null);
  };

  const handleSave = (updatedUser) => {
    console.log("User saved:", updatedUser);
    closeModals();
  };

  const handleDelete = async (id) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this user?");
    if (confirmDelete) {
      try {
        await axios.delete(`http://localhost:3001/api/user/${id}`);
        
        setUsers((prevUser) => prevUser.filter((user) => user.id !== id));
        
        setSuccessMessage("User deleted successfully!");
        setErrorMessage(""); 
      } catch (error) {
        console.error("Error deleting user:", error);
        setError("Error deleting user"); 
      }  
    };
  }
    

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-4">User List</h2>
      <div className="mb-4 flex justify-end">
        <div className="relative w-80 mr-4">
          <input
            type="text"
            placeholder="Search user ..."
            className="px-10 py-2 border border-gray-300 rounded-md w-full"
            value={searchTerm} // Bind the input to searchTerm
            onChange={(e) => setSearchTerm(e.target.value)} // Update searchTerm on input change
          />
          <FaSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
        </div>
        <button
          className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-700 flex items-center space-x-2"
          onClick={() => openAddEditModal(null)} // Open modal for new user
        >
          <FaPlus />
          <span>Add User</span>
        </button>
      </div>

      {loading && <div>Loading...</div>} {/* Display loading message while fetching users */}
      {error && <div className="text-red-500">{error}</div>} {/* Display error message if any */}

      <table className="min-w-full bg-white border border-gray-300">
        <thead>
          <tr className="bg-green-200">
            <th className="py-2 px-4 border-b border-gray-300">User Name</th>
            <th className="py-2 px-4 border-b border-gray-300">Gender</th>
            <th className="py-2 px-4 border-b border-gray-300">Address</th>
            <th className="py-2 px-4 border-b border-gray-300">Contact Number</th>
            <th className="py-2 px-4 border-b border-gray-300">Email</th>
            <th className="py-2 px-4 border-b border-gray-300">User Type</th>
            <th className="py-2 px-4 border-b border-gray-300">Action</th>
          </tr>
        </thead>
        <tbody>
          {users.length > 0 ? (
            users.map((user, index) => (
              <tr key={index} className="text-center">
                <td className="py-2 px-4 border-b border-gray-300">{user.userName}</td>
                <td className="py-2 px-4 border-b border-gray-300">{user.gender}</td>
                <td className="py-2 px-4 border-b border-gray-300">{user.address}</td>
                <td className="py-2 px-4 border-b border-gray-300">{user.contactNo}</td>
                <td className="py-2 px-4 border-b border-gray-300">{user.email}</td>
                <td className="py-2 px-4 border-b border-gray-300">{user.userType}</td>
                <td className="py-2 px-4 border-b border-gray-300">
                  <div className="flex justify-center">
                    <button
                      onClick={() => openAddEditModal(user)} // Open modal to edit user
                      className="hover:underline bg-orange-500 text-white pl-4 pr-4 py-2 rounded-md flex items-center space-x-2"
                    >
                      <FaEdit />
                      <span>Edit</span>
                    </button>
                    <button
                      onClick={() => handleDelete(user.id)}
                      className="hover:underline bg-red-500 text-white pl-4 pr-4 py-2 rounded-md flex items-center space-x-2 ml-2"
                    >
                      <FaTrash />
                      <span>Delete</span>
                    </button>
                  </div>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="7" className="text-center py-4">No users found</td>
            </tr>
          )}
        </tbody>
      </table>

      {isAddEditModalOpen && (
        <AddUserModal
          member={selectedUser} // Pass the selected user data to the modal
          onClose={closeModals}
          onSave={handleSave}
        />
      )}

      {isViewModalOpen && (
        <UserInformationModal
          user={selectedUser}
          onClose={closeModals}
        />
      )}
    </div>
  );
};

export default Users;
