import React, { useState, useEffect } from "react";
import { FaPlus, FaEye, FaTrash, FaEdit, FaSearch } from "react-icons/fa";
import MembershipInformationModal from "../components/modal/MemberProfileModal";
import MemberModal from "../components/modal/AddMemberModal";
import axios from "axios";

const Members = () => {
  const [isAddEditModalOpen, setIsAddEditModalOpen] = useState(false);
  const [isViewModalOpen, setIsViewModalOpen] = useState(false);
  const [selectedMember, setSelectedMember] = useState(null);
  const [members, setMembers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");

  const fetchMembers = async () => {
    setLoading(true);
    try {
      const params = searchTerm ? { name: searchTerm } : {};
      const response = await axios.get('http://localhost:3001/api/member-information', { params });
      if (Array.isArray(response.data)) {
        setMembers(response.data);
      } else {
        setError("Data is not an array");
      }
    } catch (err) {
      setError("Error fetching members: " + (err.response?.data?.message || err.message));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchMembers();
  }, [searchTerm]);

  const openAddEditModal = (member) => {
    setSelectedMember(member);
    setIsAddEditModalOpen(true);
  };

  const openViewModal = (member) => {
    setSelectedMember(member);
    setIsViewModalOpen(true);
  };

  const closeModals = () => {
    setIsAddEditModalOpen(false);
    setIsViewModalOpen(false);
    setSelectedMember(null);
  };

  const handleSave = () => {
    fetchMembers(); // Fetch updated members after adding/updating a member
    closeModals();
  };

  const handleDelete = (memberId) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this member?");
    if (confirmDelete) {
      setMembers((prevMembers) => prevMembers.filter((member) => member.memberId !== memberId));
      console.log("Member deleted with ID:", memberId);
    }
  };

  const filteredMembers = members.filter((member) =>
    member.fullName?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading) {
    return <div>Loading members...</div>;
  }

  if (error) {
    return <div>Error fetching members: {error}</div>;
  }

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-4">Member List</h2>
      <div className="mb-4 flex justify-end">
        <div className="relative w-80 mr-4">
          <input
            type="text"
            placeholder="Search member..."
            className="px-10 py-2 border border-gray-300 rounded-md w-full"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <FaSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
        </div>
        <button
          className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-700 flex items-center space-x-2"
          onClick={() => openAddEditModal(null)}
        >
          <FaPlus />
          <span>Add Member</span>
        </button>
      </div>

      <table className="min-w-full bg-white border border-gray-300">
        <thead>
          <tr className="bg-green-200">
            <th className="py-2 px-4 border-b border-gray-300">Member Id</th>
            <th className="py-2 px-4 border-b border-gray-300">Full Name</th>
            <th className="py-2 px-4 border-b border-gray-300">Age</th>
            <th className="py-2 px-4 border-b border-gray-300">Gender</th>
            <th className="py-2 px-4 border-b border-gray-300">Contact Number</th>
            <th className="py-2 px-4 border-b border-gray-300">Address</th>
            <th className="py-2 px-4 border-b border-gray-300">Shared Capital</th>
            <th className="py-2 px-4 border-b border-gray-300">Email</th>
            <th className="py-2 px-4 border-b border-gray-300">Password</th>
            <th className="py-2 px-4 border-b border-gray-300">Action</th>
          </tr>
        </thead>
        <tbody>
          {filteredMembers.map((member) => (
            <tr key={member.memberId} className="text-center">
              <td className="py-2 px-4 border-b border-gray-300">{member.memberId}</td>
              <td className="py-2 px-4 border-b border-gray-300">{member.fullName}</td>
              <td className="py-2 px-4 border-b border-gray-300">{member.age}</td>
              <td className="py-2 px-4 border-b border-gray-300">{member.gender}</td>
              <td className="py-2 px-4 border-b border-gray-300">{member.contactNumber}</td>
              <td className="py-2 px-4 border-b border-gray-300">{member.address}</td>
              <td className="py-2 px-4 border-b border-gray-300">{member.sharedCapital}</td>
              <td className="py-2 px-4 border-b border-gray-300">{member.email}</td>
              <td className="py-2 px-4 border-b border-gray-300">{member.password}</td>
              <td className="py-2 px-4 border-b border-gray-300 flex justify-center">
                <button className="text-blue-500" onClick={() => openViewModal(member)}>
                  <FaEye />
                </button>
                <button className="text-yellow-500 mx-2" onClick={() => openAddEditModal(member)}>
                  <FaEdit />
                </button>
                <button className="text-red-500" onClick={() => handleDelete(member.memberId)}>
                  <FaTrash />
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <MemberModal isOpen={isAddEditModalOpen} onClose={closeModals} onSave={handleSave} member={selectedMember} />
      <MembershipInformationModal isOpen={isViewModalOpen} onClose={closeModals} member={selectedMember} />
    </div>
  );
};

export default Members;
