import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const AddMemberModal = ({ isOpen, onClose, onSave, memberIdToEdit }) => {
  const [newMember, setNewMember] = useState({
    registrationType: '', memberComposition: '', dateOfRegistration: '', sharedCapital: '',
    name: { firstName: '', lastName: '', middleName: '' }, // Adjusted name structure
    maidenName: '', tin: '', dateOfBirth: '', birthPlace: '', age: '', sex: '', civilStatus: '',
    highestEducationalAttainment: '', occupation: '', sourceOfIncome: '', nameOfSpouse: '',
    spouseOccupation: '', primaryBeneficiary: '', primaryRelationship: '', primaryContact: '',
    secondaryBeneficiary: '', secondaryRelationship: '', secondaryContact: '', contactNumber: '',
    address: '', barangay: '', city: '', reference1Name: '', reference1Position: '', reference1Contact: '',
    reference2Name: '', reference2Position: '', reference2Contact: '', reference3Name: '', reference3Position: '',
    reference3Contact: '', idPicture: null, barangayClearance: null, tinFile: null
  });

  const [message, setMessage] = useState('');
  const { id } = useParams();

  const fetchMemberData = async (id) => {
    try {
      const { data } = await axios.get(`http://localhost:3001/api/members/${id}`);
      data ? setNewMember(data) : setMessage('No member data found.');
    } catch (error) {
      console.error("Error fetching member data:", error);
      setMessage('Error fetching member data. Please try again later.');
    }
  };

  useEffect(() => {
    if (isOpen && memberIdToEdit) fetchMemberData(memberIdToEdit);
    else resetForm();
  }, [isOpen, memberIdToEdit]);

  const resetForm = () => {
    setNewMember({
      registrationType: '', memberComposition: '', dateOfRegistration: '', sharedCapital: '',
      name: { firstName: '', lastName: '', middleName: '' }, // Reset name structure
      maidenName: '', tin: '', dateOfBirth: '', birthPlace: '', age: '', sex: '', civilStatus: '',
      highestEducationalAttainment: '', occupation: '', sourceOfIncome: '', nameOfSpouse: '',
      spouseOccupation: '', primaryBeneficiary: '', primaryRelationship: '', primaryContact: '',
      secondaryBeneficiary: '', secondaryRelationship: '', secondaryContact: '', contactNumber: '',
      address: '', barangay: '', city: '', reference1Name: '', reference1Position: '', reference1Contact: '',
      reference2Name: '', reference2Position: '', reference2Contact: '', reference3Name: '', reference3Position: '',
      reference3Contact: '', idPicture: null, barangayClearance: null, tinFile: null
    });
    setMessage('');
  };

  const handleFileChange = (e, fieldName) => {
    const file = e.target.files[0];
    setNewMember(prevState => ({
      ...prevState,
      [fieldName]: file
    }));
  };

  const handleAddOrEditMember = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    Object.keys(newMember).forEach(key => {
      if (newMember[key] && typeof newMember[key] !== 'object') {
        formData.append(key, newMember[key]);
      }
      // Add nested name fields if they exist
      if (typeof newMember[key] === 'object') {
        Object.keys(newMember[key]).forEach(subKey => {
          if (newMember[key][subKey]) {
            formData.append(`${key}[${subKey}]`, newMember[key][subKey]);
          }
        });
      }
    });

    try {
      const response = await axios[memberIdToEdit ? 'put' : 'post'](
        `http://localhost:3001/api/members${memberIdToEdit ? `/${memberIdToEdit}` : ''}`,
        formData, 
        { headers: { 'Content-Type': 'multipart/form-data' } }
      );
      setMessage(response.data.message || 'Member data saved successfully!');
      onSave(newMember);
      resetForm();
    } catch (error) {
      console.error("Error adding or updating member:", error);
      setMessage(`Error: ${error.response?.data.message || 'An error occurred. Please try again later.'}`);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="fixed inset-0 bg-black opacity-50" />
      <div className="bg-white rounded-lg shadow-lg p-6 w-11/12 md:w-1/2 lg:w-1/3 relative z-10 max-h-[80vh] overflow-auto">
        <div className="flex justify-end mb-2">
          <button onClick={onClose} className="text-red-500 text-2xl font-bold">&times;</button>
        </div>
        <h1 className="text-2xl font-bold text-gray-800 mb-4">MEMBER MANAGEMENT</h1>
        {message && <p className="text-red-500">{message}</p>}

        <h2 className="text-lg font-semibold mt-4 mb-2">MEMBERâ€™S TYPE REGISTRATION</h2>
        <label className="block text-gray-700">Registration Type:</label>
        <select value={newMember.registrationType} onChange={(e) => setNewMember({ ...newMember, registrationType: e.target.value })} className="mt-1 p-2 border border-gray-300 rounded-md w-full">
          <option value="">Select Type</option>
          <option value="New">New</option>
          <option value="Transfer">Transfer</option>
          <option value="Regular Member">Regular Member</option>
          <option value="Associate Member">Associate Member</option>
        </select>

        <label className="block text-gray-700 mt-4">Date of Registration:</label>
        <input type="date" value={newMember.dateOfRegistration} onChange={(e) => setNewMember({ ...newMember, dateOfRegistration: e.target.value })} className="mt-1 p-2 border border-gray-300 rounded-md w-full" />

        <label className="block text-gray-700 mt-4">Shared Capital:</label>
        <input type="number" value={newMember.sharedCapital} onChange={(e) => setNewMember({ ...newMember, sharedCapital: e.target.value })} className="mt-1 p-2 border border-gray-300 rounded-md w-full" />

        <h2 className="text-lg font-semibold mt-4 mb-2">PERSONAL INFORMATION</h2>
        <div className="grid grid-cols-2 gap-x-4 gap-y-4">
          <div>
            <label className="block text-gray-700">Full Name:</label>
            <div className="grid grid-cols-3 gap-2">
              <input type="text" value={newMember.name.lastName} onChange={(e) => setNewMember({ ...newMember, name: { ...newMember.name, lastName: e.target.value } })} placeholder="Last Name" className="mt-1 p-2 border border-gray-300 rounded-md w-full" />
              <input type="text" value={newMember.name.firstName} onChange={(e) => setNewMember({ ...newMember, name: { ...newMember.name, firstName: e.target.value } })} placeholder="First Name" className="mt-1 p-2 border border-gray-300 rounded-md w-full" />
              <input type="text" value={newMember.name.middleName} onChange={(e) => setNewMember({ ...newMember, name: { ...newMember.name, middleName: e.target.value } })} placeholder="Middle Name" className="mt-1 p-2 border border-gray-300 rounded-md w-full" />
            </div>
          </div>

          {['maidenName', 'tin', 'dateOfBirth', 'birthPlace', 'age', 'sex', 'civilStatus', 'highestEducationalAttainment', 'occupation', 'sourceOfIncome', 'nameOfSpouse', 'spouseOccupation'].map((field, idx) => (
            <div key={idx}>
              <label className="block text-gray-700">{field.replace(/([A-Z])/g, ' $1')}:</label>
              <input type={field === 'dateOfBirth' ? 'date' : 'text'} value={newMember[field]} onChange={(e) => setNewMember({ ...newMember, [field]: e.target.value })} className="mt-1 p-2 border border-gray-300 rounded-md w-full" />
            </div>
          ))}
        </div>

        <h2 className="text-lg font-semibold mt-4 mb-2">Contact Information</h2>
        {['contactNumber', 'address', 'barangay', 'city'].map((field, idx) => (
          <div key={idx}>
            <label className="block text-gray-700">{field.replace(/([A-Z])/g, ' $1')}:</label>
            <input type="text" value={newMember[field]} onChange={(e) => setNewMember({ ...newMember, [field]: e.target.value })} className="mt-1 p-2 border border-gray-300 rounded-md w-full" />
          </div>
        ))}

        <h2 className="text-lg font-semibold mt-4 mb-2">Legal Beneficiaries</h2>
        {['primaryBeneficiary', 'primaryRelationship', 'primaryContact', 'secondaryBeneficiary', 'secondaryRelationship', 'secondaryContact'].map((field, idx) => (
          <div key={idx}>
            <label className="block text-gray-700">{field.replace(/([A-Z])/g, ' $1')}:</label>
            <input type="text" value={newMember[field]} onChange={(e) => setNewMember({ ...newMember, [field]: e.target.value })} className="mt-1 p-2 border border-gray-300 rounded-md w-full" />
          </div>
        ))}

        <h2 className="text-lg font-semibold mt-4 mb-2">Character References</h2>
        {['reference1Name', 'reference1Position', 'reference1Contact', 'reference2Name', 'reference2Position', 'reference2Contact', 'reference3Name', 'reference3Position', 'reference3Contact'].map((field, idx) => (
          <div key={idx}>
            <label className="block text-gray-700">{field.replace(/([A-Z])/g, ' $1')}:</label>
            <input type="text" value={newMember[field]} onChange={(e) => setNewMember({ ...newMember, [field]: e.target.value })} className="mt-1 p-2 border border-gray-300 rounded-md w-full" />
          </div>
        ))}

        <h2 className="text-lg font-semibold mt-4 mb-2">File Upload</h2>
        {['idPicture', 'barangayClearance', 'tinFile'].map((fileField, idx) => (
          <div key={idx}>
            <label className="block text-gray-700">{fileField.replace(/([A-Z])/g, ' $1').toUpperCase()}:</label>
            <input 
              type="file" 
              onChange={(e) => handleFileChange(e, fileField)} 
              className="mt-1 p-2 border border-gray-300 rounded-md w-full" 
            />
          </div>
        ))}

        <div className="flex justify-end mt-6">
          <button onClick={handleAddOrEditMember} className="bg-blue-500 text-white p-2 rounded-md">
            Save
          </button>
        </div>
      </div>
    </div>
  );
};

export default AddMemberModal;
