import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
// import Success from '../Success';

const AddMemberModal = ({ isOpen, onClose, onSave, memberIdToEdit }) => {
  const [newMember, setNewMember] = useState({
    registrationType: '',
    memberComposition: '',
    dateOfRegistration: '',
    sharedCapital: '',
    fullName: '',
    lastName: '',
    firstName: '',
    middleName: '',
    maidenName: '',
    tin: '',
    dateOfBirth: '',
    birthPlace: '',
    age: '',
    sex: '',
    civilStatus: '',
    highestEducationalAttainment: '',
    occupation: '',
    sourceOfIncome: '',
    nameOfSpouse: '',
    spouseOccupation: '',
    primaryBeneficiary: '',
    primaryRelationship: '',
    primaryContact: '',
    secondaryBeneficiary: '',
    secondaryRelationship: '',
    secondaryContact: '',
    contactNumber: '',
    address: '',
    barangay: '',
    city: '',
    reference1Name: '',
    reference1Position: '',
    reference1Contact: '',
    reference2Name: '',
    reference2Position: '',
    reference2Contact: '',
    reference3Name: '',
    reference3Position: '',
    reference3Contact: '',
    idPicture: null,
    barangayClearance: null,
    tinFile: null,
  });

  const [message, setMessage] = useState('');
  const { id } = useParams();

  const fetchMemberData = async (id) => {
    try {
      const response = await axios.get(`http://localhost:3001/api/members/${id}`);
      if (response.data) {
        setNewMember({
          ...response.data,
        });
      } else {
        setMessage('No member data found.');
      }
    } catch (error) {
      console.error("Error fetching member data:", error);
      setMessage('Error fetching member data. Please try again later.');
    }
  };

  useEffect(() => {
    if (isOpen) {
      if (memberIdToEdit) {
        fetchMemberData(memberIdToEdit);
      } else {
        resetForm();
      }
    } else {
      resetForm();
    }
  }, [isOpen, memberIdToEdit]);

  const resetForm = () => {
    setNewMember({
      registrationType: '',
      memberComposition: '',
      dateOfRegistration: '',
      sharedCapital: '',
      fullName: '',
      lastName: '',
      firstName: '',
      middleName: '',
      maidenName: '',
      tin: '',
      dateOfBirth: '',
      birthPlace: '',
      age: '',
      sex: '',
      civilStatus: '',
      highestEducationalAttainment: '',
      occupation: '',
      sourceOfIncome: '',
      nameOfSpouse: '',
      spouseOccupation: '',
      primaryBeneficiary: '',
      primaryRelationship: '',
      primaryContact: '',
      secondaryBeneficiary: '',
      secondaryRelationship: '',
      secondaryContact: '',
      contactNumber: '',
      address: '',
      barangay: '',
      city: '',
      reference1Name: '',
      reference1Position: '',
      reference1Contact: '',
      reference2Name: '',
      reference2Position: '',
      reference2Contact: '',
      reference3Name: '',
      reference3Position: '',
      reference3Contact: '',
      idPicture: null,
      barangayClearance: null,
      tinFile: null,
    });
    setMessage('');
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setNewMember({
      ...newMember,
      [name]: files[0],
    });
  };

  const handleAddOrEditMember = async (e) => {
    e.preventDefault();

    if (!newMember.fullName || !newMember.contactNumber) {
      setMessage('Full Name and Contact Number are required.');
      return;
    }

    const formData = new FormData();
    Object.keys(newMember).forEach((key) => {
      if (newMember[key]) {
        formData.append(key, newMember[key]);
      }
    });

    try {
      const response = await axios[memberIdToEdit ? 'put' : 'post'](
        `http://localhost:3001/api/members${memberIdToEdit ? `/${memberIdToEdit}` : ''}`,
        formData,
        { headers: { 'Content-Type': 'multipart/form-data' } }
      );

      setMessage(response.data.message || 'Member data saved successfully!');
      onSave(newMember);
      resetForm();
    } catch (error) {
      console.error("Error adding or updating member:", error);
      if (error.response) {
        setMessage(`Error: ${error.response.data.message || 'An error occurred. Please try again later.'}`);
      } else {
        setMessage('Error adding or updating member. Please try again later.');
      }
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="fixed inset-0 bg-black opacity-50" />
      <div className="bg-white rounded-lg shadow-lg p-6 w-11/12 md:w-1/2 lg:w-1/3 relative z-10 max-h-[80vh] overflow-auto">
        <div className="flex justify-end mb-2">
          <button onClick={onClose} className="text-red-500 text-2xl font-bold">&times;</button>
        </div>
        <h1 className="text-2xl font-bold text-gray-800 mb-4">MEMBER MANAGEMENT</h1>
        {message && <p className="text-red-500">{message}</p>}

        <h2 className="text-lg font-semibold mt-4 mb-2">MEMBER’S TYPE REGISTRATION</h2>
        <label className="block text-gray-700">Registration Type:</label>
        <select
          value={newMember.registrationType}
          onChange={(e) => setNewMember({ ...newMember, registrationType: e.target.value })}
          className="mt-1 p-2 border border-gray-300 rounded-md w-full"
        >
          <option value="">Select Type</option>
          <option value="New">New</option>
          <option value="Transfer">Transfer</option>
          <option value="Regular Member">Regular Member</option>
          <option value="Associate Member">Associate Member</option>
        </select>

        <div>
          <label className="block text-gray-700">Member’s Composition (Member Type):</label>
          <input
            type="text"
            value={newMember.memberComposition}
            onChange={(e) => setNewMember({ ...newMember, memberComposition: e.target.value })}
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
        </div>

        <div>
          <label className="block text-gray-700">Date of Registration (Month/Day/Year):</label>
          <input
            type="date"
            value={newMember.dateOfRegistration}
            onChange={(e) => setNewMember({ ...newMember, dateOfRegistration: e.target.value })}
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
        </div>

        <div>
          <label className="block text-gray-700">Shared Capital (Initial Paid-Up):</label>
          <input
            type="number"
            value={newMember.sharedCapital}
            onChange={(e) => setNewMember({ ...newMember, sharedCapital: e.target.value })}
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
        </div>

        <h2 className="text-lg font-semibold mt-4 mb-2">PERSONAL INFORMATION</h2>
        <div className="grid grid-cols-2 gap-x-4 gap-y-4">
          <div>
            <label className="block text-gray-700">Full Name:</label>
            <div className="grid grid-cols-3 gap-2">
              <input
                type="text"
                value={newMember.lastName}
                onChange={(e) => setNewMember({ ...newMember, lastName: e.target.value })}
                placeholder="Last Name"
                className="mt-1 p-2 border border-gray-300 rounded-md w-full"
              />
              <input
                type="text"
                value={newMember.firstName}
                onChange={(e) => setNewMember({ ...newMember, firstName: e.target.value })}
                placeholder="First Name"
                className="mt-1 p-2 border border-gray-300 rounded-md w-full"
              />
              <input
                type="text"
                value={newMember.middleName}
                onChange={(e) => setNewMember({ ...newMember, middleName: e.target.value })}
                placeholder="Middle Name"
                className="mt-1 p-2 border border-gray-300 rounded-md w-full"
              />
            </div>
          </div>

          <div>
            <label className="block text-gray-700">Maiden Name (if married):</label>
            <input
              type="text"
              value={newMember.maidenName}
              onChange={(e) => setNewMember({ ...newMember, maidenName: e.target.value })}
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            />
          </div>

          <div>
            <label className="block text-gray-700">TIN Number:</label>
            <input
              type="text"
              value={newMember.tin}
              onChange={(e) => setNewMember({ ...newMember, tin: e.target.value })}
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            />
          </div>

          <div>
            <label className="block text-gray-700">Date of Birth (Month/Day/Year):</label>
            <input
              type="date"
              value={newMember.dateOfBirth}
              onChange={(e) => setNewMember({ ...newMember, dateOfBirth: e.target.value })}
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            />
          </div>

          <div>
            <label className="block text-gray-700">Birthplace (Province):</label>
            <input
              type="text"
              value={newMember.birthPlace}
              onChange={(e) => setNewMember({ ...newMember, birthPlace: e.target.value })}
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            />
          </div>

          <div>
            <label className="block text-gray-700">Age:</label>
            <input
              type="number"
              value={newMember.age}
              onChange={(e) => setNewMember({ ...newMember, age: e.target.value })}
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            />
          </div>

          <div>
            <label className="block text-gray-700">Sex:</label>
            <select
              value={newMember.sex}
              onChange={(e) => setNewMember({ ...newMember, sex: e.target.value })}
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            >
              <option value="">Select Gender</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
            </select>
          </div>

          <div>
            <label className="block text-gray-700">Civil Status:</label>
            <select
              value={newMember.civilStatus}
              onChange={(e) => setNewMember({ ...newMember, civilStatus: e.target.value })}
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            >
              <option value="">Select Civil Status</option>
              <option value="Single">Single</option>
              <option value="Married">Married</option>
              <option value="Widow">Widow</option>
              <option value="Separated">Separated</option>
            </select>
          </div>

          <div>
            <label className="block text-gray-700">Highest Educational Attainment:</label>
            <input
              type="text"
              value={newMember.highestEducationalAttainment}
              onChange={(e) => setNewMember({ ...newMember, highestEducationalAttainment: e.target.value })}
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            />
          </div>

          <div>
            <label className="block text-gray-700">Occupation / Source of Income:</label>
            <input
              type="text"
              value={newMember.occupation}
              onChange={(e) => setNewMember({ ...newMember, occupation: e.target.value })}
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            />
          </div>

          <div>
            <label className="block text-gray-700">Name of Spouse (if married):</label>
            <input
              type="text"
              value={newMember.nameOfSpouse}
              onChange={(e) => setNewMember({ ...newMember, nameOfSpouse: e.target.value })}
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            />
          </div>

          <div>
            <label className="block text-gray-700">Spouse Occupation / Source of Income:</label>
            <input
              type="text"
              value={newMember.spouseOccupation}
              onChange={(e) => setNewMember({ ...newMember, spouseOccupation: e.target.value })}
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            />
          </div>
        </div>

        <h2 className="text-lg font-semibold mt-4 mb-2">Contact Information</h2>
        <div>
          <label className="block text-gray-700">Contact Number:</label>
          <input
            type="text"
            value={newMember.contactNumber}
            onChange={(e) => setNewMember({ ...newMember, contactNumber: e.target.value })}
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
        </div>

        <div>
          <label className="block text-gray-700">Complete Address:</label>
          <input
            type="text"
            value={newMember.address}
            onChange={(e) => setNewMember({ ...newMember, address: e.target.value })}
            placeholder="House No./Street"
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
          <input
            type="text"
            value={newMember.barangay}
            onChange={(e) => setNewMember({ ...newMember, barangay: e.target.value })}
            placeholder="Barangay"
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
          <input
            type="text"
            value={newMember.city}
            onChange={(e) => setNewMember({ ...newMember, city: e.target.value })}
            placeholder="City"
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
        </div>

        <h2 className="text-lg font-semibold mt-4 mb-2">Legal Beneficiaries</h2>
        <div>
          <label className="block text-gray-700">Primary Beneficiary:</label>
          <input
            type="text"
            value={newMember.primaryBeneficiary}
            onChange={(e) => setNewMember({ ...newMember, primaryBeneficiary: e.target.value })}
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
          <input
            type="text"
            value={newMember.primaryRelationship}
            onChange={(e) => setNewMember({ ...newMember, primaryRelationship: e.target.value })}
            placeholder="Relationship"
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
          <input
            type="text"
            value={newMember.primaryContact}
            onChange={(e) => setNewMember({ ...newMember, primaryContact: e.target.value })}
            placeholder="Contact Number"
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
        </div>

        <div>
          <label className="block text-gray-700">Secondary Beneficiary:</label>
          <input
            type="text"
            value={newMember.secondaryBeneficiary}
            onChange={(e) => setNewMember({ ...newMember, secondaryBeneficiary: e.target.value })}
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
          <input
            type="text"
            value={newMember.secondaryRelationship}
            onChange={(e) => setNewMember({ ...newMember, secondaryRelationship: e.target.value })}
            placeholder="Relationship"
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
          <input
            type="text"
            value={newMember.secondaryContact}
            onChange={(e) => setNewMember({ ...newMember, secondaryContact: e.target.value })}
            placeholder="Contact Number"
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
        </div>



        <h2 className="text-lg font-semibold mt-4 mb-2">Character References</h2>
        <div>
          <label className="block text-gray-700">Reference 1 Name:</label>
          <input
            type="text"
            value={newMember.reference1Name}
            onChange={(e) => setNewMember({ ...newMember, reference1Name: e.target.value })}
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
          <input
            type="text"
            value={newMember.reference1Position}
            onChange={(e) => setNewMember({ ...newMember, reference1Position: e.target.value })}
            placeholder="Position"
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
          <input
            type="text"
            value={newMember.reference1Contact}
            onChange={(e) => setNewMember({ ...newMember, reference1Contact: e.target.value })}
            placeholder="Contact Number"
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
        </div>

        <div>
          <label className="block text-gray-700">Reference 2 Name:</label>
          <input
            type="text"
            value={newMember.reference2Name}
            onChange={(e) => setNewMember({ ...newMember, reference2Name: e.target.value })}
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
          <input
            type="text"
            value={newMember.reference2Position}
            onChange={(e) => setNewMember({ ...newMember, reference2Position: e.target.value })}
            placeholder="Position"
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
          <input
            type="text"
            value={newMember.reference2Contact}
            onChange={(e) => setNewMember({ ...newMember, reference2Contact: e.target.value })}
            placeholder="Contact Number"
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
        </div>

        <div>
          <label className="block text-gray-700">Reference 3 Name:</label>
          <input
            type="text"
            value={newMember.reference3Name}
            onChange={(e) => setNewMember({ ...newMember, reference3Name: e.target.value })}
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
          <input
            type="text"
            value={newMember.reference3Position}
            onChange={(e) => setNewMember({ ...newMember, reference3Position: e.target.value })}
            placeholder="Position"
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
          <input
            type="text"
            value={newMember.reference3Contact}
            onChange={(e) => setNewMember({ ...newMember, reference3Contact: e.target.value })}
            placeholder="Contact Number"
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
        </div>

        <div className="mt-4 flex justify-end space-x-4">
          <button onClick={onClose} className="bg-gray-300 text-gray-800 p-2 rounded-md">Cancel</button>
          <button onClick={handleAddOrEditMember} className="bg-blue-500 text-white p-2 rounded-md">Save</button>
        </div>
      </div>
    </div>
  );
};

export default AddMemberModal;
