import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AddMemberModal = ({ isOpen, onClose, onSave, idToEdit }) => {
  const [newMember, setNewMember] = useState({
    fullName: '', age: '', contactNumber: '', gender: '', address: '', sharedCapital: '', email: '', password: ''
  });
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchMemberData = async (id) => {
      try {
        const response = await axios.get(`http://localhost:3001/api/member-information/${id}`);
        setNewMember(response.data);
        console.log("Fetched member data:", response.data);
      } catch (error) {
        setMessage('ERROR FETCHING MEMBER DATA');
        console.error("Error fetching member data:", error);
      }
    };

    if (isOpen) {
      idToEdit ? fetchMemberData(idToEdit) : resetForm();
    }
  }, [isOpen, idToEdit]);

  const resetForm = () => setNewMember({
    fullName: '', age: '', contactNumber: '', gender: '', address: '', sharedCapital: '', email: '', password: ''
  });

  const handleAddOrEditMember = async (e) => {
    e.preventDefault();
    const memberData = {
      ...newMember,
      memberSince: new Date().toISOString().split('T')[0],
      sharedCapital: Number(newMember.sharedCapital) // Ensure sharedCapital is a number
    };

    // Convert string fields to uppercase except for sharedCapital
    Object.keys(memberData).forEach(key => {
      if (typeof memberData[key] === 'string' && key !== 'sharedCapital') {
        memberData[key] = memberData[key].toUpperCase();
      }
    });

    try {
      const response = await axios[idToEdit ? 'put' : 'post'](
        `http://localhost:3001/api/member-information${idToEdit ? `/${idToEdit}` : ''}`,
        memberData
      );
      setMessage(response.data.message); // Set success message
      onSave(memberData); // Trigger save callback
      resetForm(); // Reset the form
    } catch (error) {
      setMessage('ERROR ADDING OR UPDATING MEMBER');
      console.error("Error saving member data:", error);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="fixed inset-0 bg-black opacity-50" />
      <div className="bg-white rounded-lg shadow-lg p-6 w-11/12 md:w-1/2 lg:w-1/3 relative z-10">
        <div className="flex justify-end mb-2">
          <button onClick={onClose} className="text-red-500 text-2xl font-bold">&times;</button>
        </div>
        <h1 className="text-2xl font-bold text-gray-800 mb-6">MEMBER MANAGEMENT</h1>
        {message && <p className="text-green-500">{message}</p>}
        <h2 className="text-lg font-semibold mt-6 mb-2">{idToEdit ? 'EDIT MEMBER' : 'ADD NEW MEMBER'}</h2>
        <form onSubmit={handleAddOrEditMember}>
          <div className="grid grid-cols-2 gap-x-8 gap-y-4">
            {Object.keys(newMember).map((key) => (
              key === 'gender' ? (
                <div key={key}>
                  <label className="block text-gray-700">{key.toUpperCase()}:</label>
                  <select value={newMember[key]} onChange={(e) => setNewMember({ ...newMember, [key]: e.target.value })} required className="mt-1 p-2 border border-gray-300 rounded-md w-full">
                    <option value="">Select Gender</option>
                    <option value="MALE">MALE</option>
                    <option value="FEMALE">FEMALE</option>
                    <option value="OTHER">OTHER</option>
                  </select>
                </div>
              ) : (
                <div key={key}>
                  <label className="block text-gray-700">{key.toUpperCase()}:</label>
                  <input
                    type={key === 'password' ? 'password' : key === 'sharedCapital' ? 'number' : 'text'}
                    value={newMember[key]}
                    onChange={(e) => setNewMember({ ...newMember, [key]: e.target.value })}
                    required
                    className="mt-1 p-2 border border-gray-300 rounded-md w-full"
                  />
                </div>
              )
            ))}
          </div>
          <button type="submit" className="mt-4 bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600">
            {idToEdit ? 'UPDATE MEMBER' : 'ADD MEMBER'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddMemberModal;
