import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

// Reusable Input Field Component
const InputField = ({ label, type = "text", value, onChange, placeholder = "" }) => (
  <div className="mt-4">
    <label className="block text-gray-700">{label}:</label>
    <input
      type={type}
      value={value}
      onChange={onChange}
      placeholder={placeholder}
      className="mt-1 p-2 border border-gray-300 rounded-md w-full"
    />
  </div>
);

const AddMemberModal = ({ isOpen, onClose, onSave, memberIdToEdit }) => {
  const initialState = {
    registrationType: '', memberType: '', registrationDate: '', shareCapital: '',
    fullNameLastName: '', fullNameFirstName: '', fullNameMiddleName: '', maidenName: '',
    tinNumber: '', dateOfBirth: '', birthplaceProvince: '', age: '', sex: '', civilStatus: '',
    highestEducationalAttainment: '', occupationSourceOfIncome: '', spouseName: '',
    spouseOccupationSourceOfIncome: '', primaryBeneficiaryName: '', primaryBeneficiaryRelationship: '', primaryBeneficiaryContact: '',
    secondaryBeneficiaryName: '', secondaryBeneficiaryRelationship: '', secondaryBeneficiaryContact: '', contactNumber: '',
    houseNoStreet: '', barangay: '', city: '', referenceName: '', position: '', referenceContact: '', email: '', password: '',
    idPicture: null
  };

  const [newMember, setNewMember] = useState(initialState);
  const [message, setMessage] = useState('');
  const { id } = useParams();

  // Fetch Member Data for Editing
  const fetchMemberData = async (id) => {
    try {
      const { data } = await axios.get(`http://localhost:3001/api/members/${id}`);
      setNewMember(data || initialState);
    } catch (error) {
      console.error("Error fetching member data:", error);
      setMessage('Unable to fetch member data. Please try again.');
    }
  };

  useEffect(() => {
    if (isOpen && memberIdToEdit) {
      fetchMemberData(memberIdToEdit);
    } else {
      resetForm();
    }
  }, [isOpen, memberIdToEdit]);

  const resetForm = () => {
    setNewMember(initialState);
    setMessage('');
  };

  const handleFileChange = (e, fieldName) => {
    const file = e.target.files[0];
    setNewMember(prev => ({ ...prev, [fieldName]: file }));
  };

  const handleAddOrEditMember = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Object.keys(newMember).forEach(key => {
      if (newMember[key]) formData.append(key, newMember[key]);
    });

    try {
      const response = await axios[memberIdToEdit ? 'put' : 'post'](
        `http://localhost:3001/api/members${memberIdToEdit ? `/${memberIdToEdit}` : ''}`,
        formData,
        { headers: { 'Content-Type': 'multipart/form-data' } }
      );

      setMessage(response.data.message || 'Member saved successfully!');
      onSave({ ...newMember, memberId: response.data.memberId || memberIdToEdit });
      resetForm();
    } catch (error) {
      console.error("Error saving member data:", error);
      setMessage(error.response?.data.message || 'An error occurred while saving.');
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="fixed inset-0 bg-black opacity-50" />
      <div className="bg-white rounded-lg shadow-lg p-6 w-11/12 md:w-1/2 lg:w-1/3 relative z-10 max-h-[80vh] overflow-auto">
        <div className="flex justify-end mb-2">
          <button onClick={onClose} className="text-red-500 text-2xl font-bold">&times;</button>
        </div>
        <h1 className="text-2xl font-bold text-gray-800 mb-4">Member Management</h1>
        {message && <p className="text-red-500">{message}</p>}

        <h2 className="text-lg font-semibold mt-4 mb-2">Memberâ€™s Type Registration</h2>
        <InputField
          label="Registration Type"
          type="text"
          value={newMember.registrationType}
          onChange={(e) => setNewMember({ ...newMember, registrationType: e.target.value })}
        />
        <InputField
          label="Member Type"
          type="text"
          value={newMember.memberType}
          onChange={(e) => setNewMember({ ...newMember, memberType: e.target.value })}
        />
        <InputField
          label="Date of Registration"
          type="date"
          value={newMember.registrationDate}
          onChange={(e) => setNewMember({ ...newMember, registrationDate: e.target.value })}
        />
        <InputField
          label="Share Capital"
          type="number"
          value={newMember.shareCapital}
          onChange={(e) => setNewMember({ ...newMember, shareCapital: e.target.value })}
        />

        <h2 className="text-lg font-semibold mt-4 mb-2">Personal Information</h2>
        {/* Additional Fields Can Be Added Similarly */}
        <InputField
          label="Full Name"
          value={newMember.fullNameLastName}
          onChange={(e) => setNewMember({ ...newMember, fullNameLastName: e.target.value })}
        />

        <div className="mt-6 flex justify-end">
          <button
            className="bg-blue-500 text-white px-4 py-2 rounded-md"
            onClick={handleAddOrEditMember}
          >
            Save
          </button>
        </div>
      </div>
    </div>
  );
};

export default AddMemberModal;
