import React, { useState } from 'react';
import axios from 'axios';

const MemberModal = ({ isOpen, onClose, onSave }) => {
  const [newMember, setNewMember] = useState({
    fullName: '',
    age: '',
    contactNumber: '',
    gender: 'male', // Default value
    address: '',
    sharedCapital: '',
    email: '',
    password: ''
  });
  const [message, setMessage] = useState('');

  // Function to handle adding a new member
  const handleAddMember = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3001/api/member-information', newMember);
      setMessage(response.data.message);
      
      onSave(newMember); // Update the member list in the parent component

      // Reset the form
      setNewMember({
        fullName: '',
        age: '',
        contactNumber: '',
        gender: 'male', // Reset to default
        address: '',
        sharedCapital: '',
        email: '',
        password: ''
      });
    } catch (error) {
      setMessage('Error adding member');
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="fixed inset-0 bg-black opacity-50"></div>
      <div className="bg-white rounded-lg shadow-lg p-6 w-11/12 md:w-1/2 lg:w-1/3 relative z-10">
        <div className="flex justify-end mb-2">
          <button
            onClick={onClose}
            className="text-red-500 text-2xl font-bold"
          >
            &times;
          </button>
        </div>
        <h1 className="text-2xl font-bold text-gray-800 mb-6">Member Management</h1>

        {message && <p className="text-red-500">{message}</p>}

        <h2 className="text-lg font-semibold mt-6 mb-2">Add New Member</h2>
        <form onSubmit={handleAddMember}>
          <div className="grid grid-cols-2 gap-x-8 gap-y-4">
            <div>
              <label className="block text-gray-700">Full Name:</label>
              <input
                type="text"
                value={newMember.fullName}
                onChange={(e) => setNewMember({ ...newMember, fullName: e.target.value })}
                required
                className="mt-1 p-2 border border-gray-300 rounded-md w-full"
              />
            </div>

            <div>
              <label className="block text-gray-700">Age:</label>
              <input
                type="number"
                value={newMember.age}
                onChange={(e) => setNewMember({ ...newMember, age: e.target.value })}
                required
                className="mt-1 p-2 border border-gray-300 rounded-md w-full"
              />
            </div>

            <div>
              <label className="block text-gray-700">Contact Number:</label>
              <input
                type="text"
                value={newMember.contactNumber}
                onChange={(e) => setNewMember({ ...newMember, contactNumber: e.target.value })}
                required
                className="mt-1 p-2 border border-gray-300 rounded-md w-full"
              />
            </div>

            <div>
              <label className="block text-gray-700">Gender:</label>
              <select
                value={newMember.gender}
                onChange={(e) => setNewMember({ ...newMember, gender: e.target.value })}
                required
                className="mt-1 p-2 border border-gray-300 rounded-md w-full"
              >
                <option value="male">Male</option>
                <option value="female">Female</option>
              </select>
            </div>

            <div>
              <label className="block text-gray-700">Address:</label>
              <input
                type="text"
                value={newMember.address}
                onChange={(e) => setNewMember({ ...newMember, address: e.target.value })}
                required
                className="mt-1 p-2 border border-gray-300 rounded-md w-full"
              />
            </div>

            <div>
              <label className="block text-gray-700">Shared Capital:</label>
              <input
                type="number"
                value={newMember.sharedCapital}
                onChange={(e) => setNewMember({ ...newMember, sharedCapital: e.target.value })}
                required
                className="mt-1 p-2 border border-gray-300 rounded-md w-full"
              />
            </div>

            <div>
              <label className="block text-gray-700">Email:</label>
              <input
                type="email"
                value={newMember.email}
                onChange={(e) => setNewMember({ ...newMember, email: e.target.value })}
                required
                className="mt-1 p-2 border border-gray-300 rounded-md w-full"
              />
            </div>

            <div>
              <label className="block text-gray-700">Password:</label>
              <input
                type="password"
                value={newMember.password}
                onChange={(e) => setNewMember({ ...newMember, password: e.target.value })}
                required
                className="mt-1 p-2 border border-gray-300 rounded-md w-full"
              />
            </div>
          </div>

          <button
            type="submit"
            className="mt-4 bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600"
          >
            Add Member
          </button>
        </form>
      </div>
    </div>
  );
};

export default MemberModal;
