import axios from "axios";
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const AdduserModal = ({ isOpen, onClose, onSave, userIdToEdit }) => {
  const [newUser, setNewUser] = useState({
    fullName: '',
    age: '',
    gender: '',
    address: '',
    contactNumber: '',
    email: '',
    password: '',
    userType: ''

  });

  const [message, setMessage] = useState('')
  const {id} = useParams()


  const fetchUserData = async (id) => {
    try{
      const response = await axios.get(`http://localhost:3001/api/user/${id}`)
      if (response.data){
        setNewUser({
          fullName: response.data.fullName,
          age: response.data.age,
          gender: response.data.gender,
          address: response.data.address,
          contactNumber: response.data.contactNumber,
          email: response.data.email,
          passwordl: response.data.password,
          userType: response.data.userType
        })
      }else{
        setMessage('No user found')
      }
    }catch (error) { 
      setMessage('Error fetching user data')
    }
  }


  // Effect to update formData when member changes
  useEffect(() => {
    if (isOpen){
      if(userIdToEdit){
        fetchUserData(userIdToEdit)
      }else{
        resetForm()
      }
    }else{
      resetForm()
    }
  }, [isOpen, userIdToEdit]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSave = () => {
    onSave(formData);
    onClose();
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="fixed inset-0 bg-black opacity-50"></div>
      <div className="bg-white rounded-lg shadow-lg p-6 w-1/2 relative z-10">
        <div className="flex justify-end mb-2">
          <button
            onClick={onClose}
            className="text-red-500 text-2xl font-bold"
          >
            &times;
          </button>
        </div>
        <h1 className="text-2xl font-bold text-gray-800 mb-6">User Management</h1>
        <p className="text-xl font-bold text-gray-800 mb-6">User Information</p>

        <div className="grid grid-cols-2 gap-x-8 gap-y-4">
          <div>
            <label className="block text-gray-700">Full Name:</label>
            <input
              type="text"
              name="fullName"
              value={formData.fullName || ''}
              onChange={handleChange}
              placeholder="Enter full name"
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            />
          </div>

          <div>
            <label className="block text-gray-700">Age:</label>
            <input
              type="number"
              name="age"
              value={formData.age || ''}
              onChange={handleChange}
              placeholder="Enter age"
              className="mt-1 p-2 border border-gray-300 rounded-md max-w-sm"
            />
          </div>

          <div>
            <label className="block text-gray-700">Contact Number:</label>
            <input
              type="tel"
              name="contact"
              value={formData.contact || ''}
              onChange={handleChange}
              placeholder="Enter contact number"
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            />
          </div>

          <div>
            <label className="block text-gray-700">Gender:</label>
            <select
              name="gender"
              value={formData.gender || ''}
              onChange={handleChange}
              className="mt-1 p-2 border border-gray-300 rounded-md max-w-sm"
            >
              <option value="male">Male</option>
              <option value="female">Female</option>
            </select>
          </div>

          <div>
            <label className="block text-gray-700">Address:</label>
            <input
              type="text"
              name="address"
              value={formData.address || ''}
              onChange={handleChange}
              placeholder="Enter address"
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            />
          </div>

          <div>
            <label className="block text-gray-700">User Type:</label>
            <select
              name="userType"
              value={formData.userType || ''}
              onChange={handleChange}
              className="mt-1 p-2 border border-gray-300 rounded-md max-w-sm"
            >
              <option value="Admin">Admin</option>
              <option value="User">User</option>
            </select>
          </div>

          <div>
            <label className="block text-gray-700">Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email || ''}
              onChange={handleChange}
              placeholder="Enter email"
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            />
          </div>

          <div>
            <label className="block text-gray-700">Password:</label>
            <input
              type="password"
              name="password"
              value={formData.password || ''}
              onChange={handleChange}
              placeholder="Enter password"
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            />
          </div>
        </div>

        <div className="mt-6 flex justify-end">
          <button
            onClick={handleSave}
            className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-700 mr-2"
          >
            Save
          </button>
          <button
            onClick={onClose}
            className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-700"
          >
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default UserModal;
