import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AddMemberModal = ({ isOpen, onClose, onSave, lastMemberId }) => {
  const [newMember, setNewMember] = useState({
    fullName: '',
    age: '',
    contactNumber: '',
    gender: '',
    address: '',
    sharedCapital: '',
    email: '',
    password: ''
  });
  const [memberId, setMemberId] = useState(''); // To store the generated memberId
  const [message, setMessage] = useState('');

  // Function to format the date to "YYYY-MM-DD"
  const formatDate = (date) => {
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
  };

  // Function to handle adding a new member
  const handleAddMember = async (e) => {
    e.preventDefault();

    // Convert all input values to uppercase
    const memberData = {
      ...newMember,
      memberSince: formatDate(new Date()), // Format the memberSince date
    };

    // Convert all values to uppercase
    for (const key in memberData) {
      if (typeof memberData[key] === 'string') {
        memberData[key] = memberData[key].toUpperCase();
      }
    }

    try {
      const response = await axios.post('http://localhost:3001/api/member-information', memberData);
      setMessage(response.data.message);
      setMemberId(response.data.memberId); // Capture the new memberId

      // Call the onSave function to update the member list in the parent component
      onSave(memberData); // Pass the new member data to the parent component

      // Reset the newMember state
      setNewMember({
        fullName: '',
        age: '',
        contactNumber: '',
        gender: '',
        address: '',
        sharedCapital: '',
        email: '',
        password: ''
      });
    } catch (error) {
      setMessage('ERROR ADDING MEMBER');
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="fixed inset-0 bg-black opacity-50"></div>
      <div className="bg-white rounded-lg shadow-lg p-6 w-11/12 md:w-1/2 lg:w-1/3 relative z-10">
        <div className="flex justify-end mb-2">
          <button
            onClick={onClose}
            className="text-red-500 text-2xl font-bold"
          >
            &times;
          </button>
        </div>
        <h1 className="text-2xl font-bold text-gray-800 mb-6">MEMBER MANAGEMENT</h1>

        {message && <p className="text-green-500">{message}</p>}
        {memberId && <p className="text-blue-500">NEW MEMBER ID: {memberId}</p>}

        <h2 className="text-lg font-semibold mt-6 mb-2">ADD NEW MEMBER</h2>
        <form onSubmit={handleAddMember}>
          <div className="grid grid-cols-2 gap-x-8 gap-y-4">
            {Object.keys(newMember).map((key) => (
              <div key={key}>
                <label className="block text-gray-700">
                  {key.toUpperCase()}:
                </label>
                <input
                  type="text"
                  value={newMember[key]}
                  onChange={(e) => setNewMember({ ...newMember, [key]: e.target.value })}
                  required
                  className="mt-1 p-2 border border-gray-300 rounded-md w-full"
                />
              </div>
            ))}
          </div>
          <button
            type="submit"
            className="mt-4 bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600"
          >
            ADD MEMBER
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddMemberModal;
