                    <p>{member.accountStatus}</p>
                    </div>
                </div>

                {/* Button to activate account */}
                {member.accountStatus !== 'Active' && (
                    <button
                    onClick={async () => {
                        try {
                        // Assuming you have an API endpoint to activate the account
                        await axios.put(`http://localhost:3001/api/members/activate/${member.id}`);
                        // After activation, update the account status locally
                        setMember((prevMember) => ({
                            ...prevMember,
                            accountStatus: 'Active',
                        }));
                        setSuccessMessage("Account activated successfully!");
                        } catch (error) {
                        console.error("Error activating account:", error);
                        setErrorMessage("Error activating account.");
                        }
                    }}
                    className="mt-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
                    >
