import React, { useState, useEffect } from "react";
import axios from "axios";
import { FaPlus, FaTrash, FaEdit, FaSearch } from "react-icons/fa";
import AddUserModal from "../components/modal/AddUserModal";
import UserInformationModal from "../components/modal/MemberProfileModal";

const Users = () => {
  const [users, setUsers] = useState([]); // State to store the users
  const [loading, setLoading] = useState(false); // State to handle loading
  const [error, setError] = useState(null); // State to handle errors
  const [successMessage, setSuccessMessage] = useState(""); // State for success messages
  const [searchTerm, setSearchTerm] = useState(""); // State for search term
  const [isAddEditModalOpen, setIsAddEditModalOpen] = useState(false);
  const [isViewModalOpen, setIsViewModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);

  const apiClient = axios.create({
    baseURL: "http://localhost:3001/api",
  });

  // Fetch users from the API
  const fetchUsers = async () => {
    setLoading(true);
    setError(null);
    setSuccessMessage("");

    try {
      const params = searchTerm ? { name: searchTerm } : {};
      const response = await apiClient.get("/users", { params });
      setUsers(response.data);
    } catch (err) {
      setError(err.response?.data?.message || "Failed to fetch users.");
    } finally {
      setLoading(false);
    }
  };

  // Fetch specific user details
  const fetchUser = async (userId) => {
    setLoading(true);
    setError(null);

    try {
      const response = await apiClient.get(`/users/${userId}`);
      setSelectedUser(response.data);
      setIsAddEditModalOpen(true);
    } catch (err) {
      setError(err.response?.data?.message || "Failed to fetch user details.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, [searchTerm]);

  const openAddEditModal = (user) => {
    if (user) fetchUser(user._id); // Fetch user details for editing
    else {
      setSelectedUser(null); // Reset selected user for adding
      setIsAddEditModalOpen(true);
    }
  };

  const closeModals = () => {
    setIsAddEditModalOpen(false);
    setIsViewModalOpen(false);
    setSelectedUser(null);
  };

  const handleSave = (updatedUser) => {
    console.log("User saved:", updatedUser);
    fetchUsers(); // Refresh the user list after saving
    closeModals();
  };

  const handleDelete = async (userId) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this user?");
    if (!confirmDelete) return;

    setLoading(true);
    setError(null);

    try {
      await apiClient.delete(`/users/${userId}`);
      setUsers((prevUsers) => prevUsers.filter((user) => user._id !== userId));
      setSuccessMessage("User deleted successfully!");
    } catch (err) {
      setError(err.response?.data?.message || "Failed to delete user.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-4">User List</h2>

      <div className="mb-4 flex justify-between">
        <div className="relative w-80">
          <input
            type="text"
            placeholder="Search user ..."
            className="px-10 py-2 border border-gray-300 rounded-md w-full"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <FaSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
        </div>

        <button
          className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-700 flex items-center space-x-2"
          onClick={() => openAddEditModal(null)}
        >
          <FaPlus />
          <span>Add User</span>
        </button>
      </div>

      {loading && <div>Loading...</div>}
      {error && <div className="text-red-500">{error}</div>}
      {successMessage && <div className="text-green-500">{successMessage}</div>}

      <table className="min-w-full bg-white border border-gray-300">
        <thead>
          <tr className="bg-green-200">
            <th className="py-2 px-4 border-b">User Name</th>
            <th className="py-2 px-4 border-b">Gender</th>
            <th className="py-2 px-4 border-b">Address</th>
            <th className="py-2 px-4 border-b">Contact Number</th>
            <th className="py-2 px-4 border-b">Email</th>
            <th className="py-2 px-4 border-b">User Type</th>
            <th className="py-2 px-4 border-b">Action</th>
          </tr>
        </thead>
        <tbody>
          {users.length > 0 ? (
            users.map((user) => (
              <tr key={user.id} className="text-center">
                <td className="py-2 px-4 border-b">{user.userName}</td>
                <td className="py-2 px-4 border-b">{user.gender}</td>
                <td className="py-2 px-4 border-b">{user.address}</td>
                <td className="py-2 px-4 border-b">{user.contactNo}</td>
                <td className="py-2 px-4 border-b">{user.email}</td>
                <td className="py-2 px-4 border-b">{user.userType}</td>
                <td className="py-2 px-4 border-b">
                  <div className="flex justify-center space-x-2">
                    <button
                      onClick={() => openAddEditModal(user)}
                      className="bg-orange-500 text-white px-4 py-2 rounded hover:bg-orange-700 flex items-center"
                    >
                      <FaEdit />
                      <span>Edit</span>
                    </button>
                    <button
                      onClick={() => handleDelete(user._id)}
                      className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-700 flex items-center"
                    >
                      <FaTrash />
                      <span>Delete</span>
                    </button>
                  </div>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="7" className="text-center py-4">
                No users found
              </td>
            </tr>
          )}
        </tbody>
      </table>

      {isAddEditModalOpen && (
        <AddUserModal member={selectedUser} onClose={closeModals} onSave={handleSave} />
      )}
      {isViewModalOpen && <UserInformationModal user={selectedUser} onClose={closeModals} />}
    </div>
  );
};

export default Users;
