import React, { useState, useEffect } from 'react';

const Members = () => {
  const [members, setMembers] = useState([]); // State to hold the members data
  const [searchQuery, setSearchQuery] = useState(''); // State to hold the search input

  // Simulated fetching of member data (replace with your API call)
  useEffect(() => {
    const fetchMembers = async () => {
      // Replace with your actual API call
      const response = await fetch('/api/members'); // Example endpoint
      const data = await response.json();
      setMembers(data); // Update state with fetched members
    };

    fetchMembers();
  }, []); // Empty dependency array means this effect runs once on mount

  // Filter members based on search query
  const filteredMembers = members.filter((member) =>
    member.name?.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Members List</h1>
      <input
        type="text"
        placeholder="Search members by name"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        className="border p-2 mb-4 w-full"
      />
      <table className="min-w-full border">
        <thead>
          <tr className="bg-gray-200">
            <th className="border px-4 py-2">Name</th>
            <th className="border px-4 py-2">Email</th>
            <th className="border px-4 py-2">Phone</th>
            {/* Add other headers as needed */}
          </tr>
        </thead>
        <tbody>
          {filteredMembers.length > 0 ? (
            filteredMembers.map((member, index) => (
              <tr key={index} className="hover:bg-gray-100">
                <td className="border px-4 py-2">{member.name}</td>
                <td className="border px-4 py-2">{member.email}</td>
                <td className="border px-4 py-2">{member.phone}</td>
                {/* Add other member properties as needed */}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="3" className="border px-4 py-2 text-center">
                No members found
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Members;
