import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

// Reusable input field component for cleaner code
const InputField = ({ label, type = 'text', value, onChange, placeholder }) => (
  <div className="mt-4">
    <label className="block text-gray-700">{label}:</label>
    <input
      type={type}
      value={value}
      onChange={onChange}
      placeholder={placeholder}
      className="mt-1 p-2 border border-gray-300 rounded-md w-full"
    />
  </div>
);

const AddMemberModal = ({ isOpen, onClose, onSave, memberIdToEdit }) => {
  const [newMember, setNewMember] = useState({
    registrationType: '', memberType: '', registrationDate: '', shareCapital: '',
    fullNameLastName: '', fullNameFirstName: '', fullNameMiddleName: '', maidenName: '',
    tinNumber: '', dateOfBirth: '', birthplaceProvince: '', age: '', sex: '', civilStatus: '',
    highestEducationalAttainment: '', occupationSourceOfIncome: '', spouseName: '',
    spouseOccupationSourceOfIncome: '', primaryBeneficiaryName: '', primaryBeneficiaryRelationship: '', primaryBeneficiaryContact: '',
    secondaryBeneficiaryName: '', secondaryBeneficiaryRelationship: '', secondaryBeneficiaryContact: '', contactNumber: '',
    houseNoStreet: '', barangay: '', city: '', referenceName: '', position: '', referenceContact: '', email: '', password: '',
    idPicture: null
  });

  const [message, setMessage] = useState('');
  const { id } = useParams();

  // Fetch member data for editing
  const fetchMemberData = async (id) => {
    try {
      const { data } = await axios.get(`http://localhost:3001/api/members/${id}`);
      if (data) {
        setNewMember(data);
        setMessage('');
      } else {
        setMessage('No member data found.');
      }
    } catch (error) {
      console.error('Error fetching member data:', error);
      setMessage('Error fetching member data. Please try again later.');
    }
  };

  // Reset form state
  const resetForm = () => {
    setNewMember({
      registrationType: '', memberType: '', registrationDate: '', shareCapital: '',
      fullNameLastName: '', fullNameFirstName: '', fullNameMiddleName: '', maidenName: '',
      tinNumber: '', dateOfBirth: '', birthplaceProvince: '', age: '', sex: '', civilStatus: '',
      highestEducationalAttainment: '', occupationSourceOfIncome: '', spouseName: '',
      spouseOccupationSourceOfIncome: '', primaryBeneficiaryName: '', primaryBeneficiaryRelationship: '', primaryBeneficiaryContact: '',
      secondaryBeneficiaryName: '', secondaryBeneficiaryRelationship: '', secondaryBeneficiaryContact: '', contactNumber: '',
      houseNoStreet: '', barangay: '', city: '', referenceName: '', position: '', referenceContact: '', email: '', password: '',
      idPicture: null
    });
    setMessage('');
  };

  // Handle file uploads
  const handleFileChange = (e, fieldName) => {
    const file = e.target.files[0];
    if (file && file.size > 2 * 1024 * 1024) {
      setMessage('File size exceeds 2MB limit.');
      return;
    }
    setNewMember(prevState => ({
      ...prevState,
      [fieldName]: file
    }));
  };

  // Save or update member data
  const handleAddOrEditMember = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    Object.keys(newMember).forEach(key => {
      if (newMember[key] && typeof newMember[key] !== 'object') {
        formData.append(key, newMember[key]);
      }
    });

    try {
      const response = await axios[memberIdToEdit ? 'put' : 'post'](
        `http://localhost:3001/api/members${memberIdToEdit ? `/${memberIdToEdit}` : ''}`,
        formData,
        { headers: { 'Content-Type': 'multipart/form-data' } }
      );

      setMessage(response.data.message || 'Member data saved successfully!');
      const updatedMember = { ...newMember, memberId: response.data.memberId || memberIdToEdit };
      onSave(updatedMember);
      resetForm();
    } catch (error) {
      console.error('Error adding or updating member:', error);
      setMessage(`Error: ${error.response?.data.message || 'An error occurred. Please try again later.'}`);
    }
  };

  useEffect(() => {
    if (isOpen && memberIdToEdit) {
      fetchMemberData(memberIdToEdit);
    } else {
      resetForm();
    }
  }, [isOpen, memberIdToEdit]);

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="fixed inset-0 bg-black opacity-50" />
      <div className="bg-white rounded-lg shadow-lg p-6 w-11/12 md:w-1/2 lg:w-1/3 relative z-10 max-h-[80vh] overflow-auto">
        <div className="flex justify-end mb-2">
          <button onClick={onClose} className="text-red-500 text-2xl font-bold" aria-label="Close modal">&times;</button>
        </div>
        <h1 className="text-2xl font-bold text-gray-800 mb-4">Member Management</h1>
        {message && <p className="text-red-500">{message}</p>}

        {/* Registration Type */}
        <label className="block text-gray-700">Registration Type:</label>
        <select
          value={newMember.registrationType}
          onChange={(e) => setNewMember({ ...newMember, registrationType: e.target.value })}
          className="mt-1 p-2 border border-gray-300 rounded-md w-full"
        >
          <option value="">Select Type</option>
          <option value="New">New</option>
          <option value="Transfer">Transfer</option>
          <option value="Regular Member">Regular Member</option>
          <option value="Associate Member">Associate Member</option>
        </select>

        {/* Other sections can reuse the InputField component */}
        <InputField
          label="Member Type"
          type="text"
          value={newMember.memberType}
          onChange={(e) => setNewMember({ ...newMember, memberType: e.target.value })}
        />
        {/* Add similar InputField usages for other fields */}
        
        <div className="mt-6 flex justify-end">
          <button className="bg-blue-500 text-white px-4 py-2 rounded-md" onClick={handleAddOrEditMember}>Save</button>
        </div>
      </div>
    </div>
  );
};

export default AddMemberModal;
