import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import Success from '../Success';

const AddMemberModal = ({ isOpen, onClose, onSave, memberIdToEdit }) => {
  const [newMember, setNewMember] = useState({
    registrationType: '', memberType: '', registrationDate: '', shareCapital: '',
    fullNameLastName: '', fullNameFirstName: '', fullNameMiddleName: '', maidenName: '',
    tinNumber: '', dateOfBirth: '', birthplaceProvince: '', age: '', sex: '', civilStatus: '',
    highestEducationalAttainment: '', occupationSourceOfIncome: '', spouseName: '',
    spouseOccupationSourceOfIncome: '', primaryBeneficiaryName: '', primaryBeneficiaryRelationship: '', primaryBeneficiaryContact: '',
    secondaryBeneficiaryName: '', secondaryBeneficiaryRelationship: '', secondaryBeneficiaryContact: '', contactNumber: '',
    houseNoStreet: '', barangay: '', city: '', referenceName: '', position: '', referenceContact: '', email: '', password: '',
    idPicture: null
  });

  const [isEditMode, setIsEditMode] = useState(false);
  const [message, setMessage] = useState('');
  const { id } = useParams();

  useEffect(() => {
    if (!isOpen) {
      resetForm();
    } else if (memberIdToEdit) {
      setIsEditMode(true);
      fetchMemberData(memberIdToEdit);
    } else {
      setIsEditMode(false);
    }
  }, [isOpen, memberIdToEdit]);

  const fetchMemberData = async (id) => {
    try {
      const { data } = await axios.get(`http://localhost:3001/api/members/${id}`);
      setNewMember(data || {});
    } catch (error) {
      console.error('Error fetching member data:', error);
      setMessage('Error fetching member data. Please try again.');
    }
  };

  const resetForm = () => {
    setNewMember({});
    setMessage('');
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewMember((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    const { name } = e.target;
    const file = e.target.files[0];
    setNewMember((prev) => ({ ...prev, [name]: file }));
  };

  const validateForm = () => {
    // Example: Validate required fields (you can add more specific validations)
    const requiredFields = ['registrationType', 'memberType', 'fullNameLastName', 'email', 'password'];
    for (const field of requiredFields) {
      if (!newMember[field]) {
        setMessage(`Field "${field.replace(/([A-Z])/g, ' $1')}" is required.`);
        return false;
      }
    }
    return true;
  };

  const handleAddOrEditMember = async (event) => {
    event.preventDefault();
    if (isSubmitting || !validateForm()) return;

    setIsSubmitting(true);
    const formData = new FormData();
    Object.keys(newMember).forEach((key) => {
      if (newMember[key]) formData.append(key, newMember[key]);
    });

    try {
      const response = isEditMode
        ? await axios.put(`http://localhost:3001/api/members/${memberIdToEdit}`, formData, {
            headers: { 'Content-Type': 'multipart/form-data' },
          })
        : await axios.post('http://localhost:3001/api/members', formData, {
            headers: { 'Content-Type': 'multipart/form-data' },
          });

      alert(response.data.message || (isEditMode ? 'Member updated successfully!' : 'Member added successfully!'));
      resetForm();
      onSave(); // Call callback to refresh member list
      onClose();
    } catch (error) {
      console.error('Error:', error);
      alert(error.response?.data?.message || 'An error occurred.');
    } finally {
      setIsSubmitting(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="fixed inset-0 bg-black opacity-50" />
      <div className="bg-white rounded-lg shadow-lg p-6 w-11/12 md:w-1/2 lg:w-1/3 relative z-10 max-h-[80vh] overflow-auto">
        <div className="flex justify-end mb-2">
          <button onClick={onClose} className="text-red-500 text-2xl font-bold">
            &times;
          </button>
        </div>
        <h1 className="text-2xl font-bold text-gray-800 mb-4">
          {isEditMode ? 'Edit Member' : 'Add Member'}
        </h1>
        {message && <p className="text-red-500 mb-4">{message}</p>}

        <form onSubmit={handleAddOrEditMember}>
          {/* Registration Type */}
          <label className="block text-gray-700">Registration Type:</label>
          <select
            name="registrationType"
            value={newMember.registrationType || ''}
            onChange={handleInputChange}
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          >
            <option value="">Select Type</option>
            <option value="New">New</option>
            <option value="Transfer">Transfer</option>
            <option value="Regular Member">Regular Member</option>
            <option value="Associate Member">Associate Member</option>
          </select>

          {/* Dynamic Fields */}
          {['memberType', 'fullNameLastName', 'email', 'password'].map((field) => (
            <div key={field} className="mt-4">
              <label className="block text-gray-700">{field.replace(/([A-Z])/g, ' $1')}:</label>
              <input
                type={field === 'password' ? 'password' : 'text'}
                name={field}
                value={newMember[field] || ''}
                onChange={handleInputChange}
                className="mt-1 p-2 border border-gray-300 rounded-md w-full"
              />
            </div>
          ))}

          {/* File Upload */}
          <div className="mt-4">
            <label className="block text-gray-700">Upload ID Picture:</label>
            <input
              type="file"
              name="idPicture"
              onChange={handleFileChange}
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
            />
          </div>

          <div className="mt-6 flex justify-end">
            <button type="button" className="bg-red-500 text-white px-4 py-2 rounded-md" onClick={onClose}>
              Cancel
            </button>
            <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded-md">
              {isSubmitting ? 'Saving...' : 'Save'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddMemberModal;
