import React, { useState } from 'react';
import axios from 'axios';

const MemberModal = ({ isOpen, onClose, onSave, member }) => {
  const [newMember, setNewMember] = useState({
    memberId: member?.memberId || '',
    fullName: member?.fullName || '',
    age: member?.age || '',
    contactNumber: member?.contactNumber || '',
    gender: member?.gender || '',
    address: member?.address || '',
    sharedCapital: member?.sharedCapital || '',
    email: member?.email || '',
    password: member?.password || ''
  });
  const [message, setMessage] = useState('');

  // Function to handle adding or updating a member
  const handleAddMember = async (e) => {
    e.preventDefault();
    try {
      const url = member ? `http://localhost:3001/api/member-information/${member.memberId}` : 'http://localhost:3001/api/member-information';
      const method = member ? 'put' : 'post';
      const response = await axios[method](url, newMember);

      setMessage(response.data.message);
      setNewMember({
        memberId: '',
        fullName: '',
        age: '',
        contactNumber: '',
        gender: '',
        address: '',
        sharedCapital: '',
        email: '',
        password: ''
      });
      
      // Call onSave to fetch the updated member list
      onSave(); 
    } catch (error) {
      setMessage('Error saving member');
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="fixed inset-0 bg-black opacity-50"></div>
      <div className="bg-white rounded-lg shadow-lg p-6 w-11/12 md:w-1/2 lg:w-1/3 relative z-10">
        <div className="flex justify-end mb-2">
          <button onClick={onClose} className="text-red-500 text-2xl font-bold">&times;</button>
        </div>
        <h1 className="text-2xl font-bold text-gray-800 mb-6">Member Management</h1>
        {message && <p className="text-red-500">{message}</p>}
        
        <h2 className="text-lg font-semibold mt-6 mb-2">Add/Edit Member</h2>
        <form onSubmit={handleAddMember}>
          <div className="grid grid-cols-2 gap-x-8 gap-y-4">
            {Object.keys(newMember).map((key) => (
              <div key={key}>
                <label className="block text-gray-700">{key.charAt(0).toUpperCase() + key.slice(1)}:</label>
                <input
                  type="text"
                  value={newMember[key]}
                  onChange={(e) => setNewMember({ ...newMember, [key]: e.target.value })}
                  required
                  className="mt-1 p-2 border border-gray-300 rounded-md w-full"
                />
              </div>
            ))}
          </div>
          <button type="submit" className="mt-4 bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600">
            {member ? 'Update Member' : 'Add Member'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default MemberModal;
