import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const AddMemberModal = ({ isOpen, onClose, onSave, memberIdToEdit }) => {
  const [newMember, setNewMember] = useState({
    registrationType: '', memberComposition: '', dateOfRegistration: '', sharedCapital: '', fullName: '',
    lastName: '', firstName: '', middleName: '', maidenName: '', tin: '', dateOfBirth: '', birthPlace: '',
    age: '', sex: '', civilStatus: '', highestEducationalAttainment: '', occupation: '', sourceOfIncome: '',
    nameOfSpouse: '', spouseOccupation: '', primaryBeneficiary: '', primaryRelationship: '', primaryContact: '',
    secondaryBeneficiary: '', secondaryRelationship: '', secondaryContact: '', contactNumber: '', address: '',
    barangay: '', city: '', reference1Name: '', reference1Position: '', reference1Contact: '', reference2Name: '',
    reference2Position: '', reference2Contact: '', reference3Name: '', reference3Position: '', reference3Contact: '',
    idPicture: null, barangayClearance: null, tinFile: null
  });

  const [message, setMessage] = useState('');
  const { id } = useParams();

  const fetchMemberData = async (id) => {
    try {
      const { data } = await axios.get(`http://localhost:3001/api/members/${id}`);
      data ? setNewMember({ ...data }) : setMessage('No member data found.');
    } catch (error) {
      setMessage('Error fetching member data.');
    }
  };

  useEffect(() => {
    if (isOpen && memberIdToEdit) fetchMemberData(memberIdToEdit);
    else resetForm();
  }, [isOpen, memberIdToEdit]);

  const resetForm = () => {
    setNewMember({
      registrationType: '', memberComposition: '', dateOfRegistration: '', sharedCapital: '', fullName: '',
      lastName: '', firstName: '', middleName: '', maidenName: '', tin: '', dateOfBirth: '', birthPlace: '',
      age: '', sex: '', civilStatus: '', highestEducationalAttainment: '', occupation: '', sourceOfIncome: '',
      nameOfSpouse: '', spouseOccupation: '', primaryBeneficiary: '', primaryRelationship: '', primaryContact: '',
      secondaryBeneficiary: '', secondaryRelationship: '', secondaryContact: '', contactNumber: '', address: '',
      barangay: '', city: '', reference1Name: '', reference1Position: '', reference1Contact: '', reference2Name: '',
      reference2Position: '', reference2Contact: '', reference3Name: '', reference3Position: '', reference3Contact: '',
      idPicture: null, barangayClearance: null, tinFile: null
    });
    setMessage('');
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setNewMember(prev => ({ ...prev, [name]: files[0] }));
  };

  const handleAddOrEditMember = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Object.entries(newMember).forEach(([key, value]) => value && formData.append(key, value));

    try {
      const response = await axios[memberIdToEdit ? 'put' : 'post'](
        `http://localhost:3001/api/members${memberIdToEdit ? `/${memberIdToEdit}` : ''}`,
        formData,
        { headers: { 'Content-Type': 'multipart/form-data' } }
      );
      setMessage(response.data.message || 'Member data saved successfully!');
      onSave(newMember);
      resetForm();
    } catch (error) {
      setMessage(error.response?.data?.message || 'Error adding or updating member.');
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="fixed inset-0 bg-black opacity-50" />
      <div className="bg-white rounded-lg shadow-lg p-6 w-11/12 md:w-1/2 lg:w-1/3 relative z-10 max-h-[80vh] overflow-auto">
        <div className="flex justify-end mb-2">
          <button onClick={onClose} className="text-red-500 text-2xl font-bold">&times;</button>
        </div>
        <h1 className="text-2xl font-bold text-gray-800 mb-4">MEMBER MANAGEMENT</h1>
        {message && <p className="text-red-500">{message}</p>}
        {/* Dynamic Input Fields */}
        {[
          ['Registration Type', 'registrationType', ['New', 'Transfer', 'Regular Member', 'Associate Member']],
          ['Member Composition', 'memberComposition'],
          ['Date of Registration', 'dateOfRegistration', 'date'],
          ['Shared Capital', 'sharedCapital', 'number'],
          ['Full Name', 'fullName'], ['TIN Number', 'tin'], ['Date of Birth', 'dateOfBirth', 'date'],
          ['Birthplace', 'birthPlace'], ['Age', 'age', 'number'], ['Sex', 'sex', ['Male', 'Female']],
          ['Civil Status', 'civilStatus', ['Single', 'Married', 'Widow', 'Separated']],
          ['Highest Educational Attainment', 'highestEducationalAttainment'],
          ['Occupation', 'occupation'], ['Spouse Occupation', 'spouseOccupation'],
          ['Name of Spouse', 'nameOfSpouse'], ['Primary Beneficiary', 'primaryBeneficiary'],
          ['Secondary Beneficiary', 'secondaryBeneficiary']
        ].map(([label, key, type = 'text', options]) => (
          <div key={key}>
            <label className="block text-gray-700">{label}:</label>
            {options ? (
              <select
                value={newMember[key]}
                onChange={(e) => setNewMember({ ...newMember, [key]: e.target.value })}
                className="mt-1 p-2 border border-gray-300 rounded-md w-full"
              >
                <option value="">Select {label}</option>
                {options.map(option => <option key={option} value={option}>{option}</option>)}
              </select>
            ) : (
              <input
                type={type}
                value={newMember[key]}
                onChange={(e) => setNewMember({ ...newMember, [key]: e.target.value })}
                className="mt-1 p-2 border border-gray-300 rounded-md w-full"
              />
            )}
          </div>
        ))}
        <h2 className="text-lg font-semibold mt-4 mb-2">Contact Information</h2>
        <div>
          <label className="block text-gray-700">Contact Number:</label>
          <input
            type="text"
            value={newMember.contactNumber}
            onChange={(e) => setNewMember({ ...newMember, contactNumber: e.target.value })}
            className="mt-1 p-2 border border-gray-300 rounded-md w-full"
          />
        </div>
        {/* Beneficiaries and References handled dynamically as above */}
        <div className="flex justify-end mt-6">
          <button onClick={handleAddOrEditMember} className="bg-blue-500 text-white p-2 rounded-md">
            Save
          </button>
        </div>
      </div>
    </div>
  );
};

export default AddMemberModal;
