import React, { useState, useEffect } from "react";
import axios from "axios";

const MemberModal = ({ member, onClose, onSave }) => {
  const [formData, setFormData] = useState({
    memberId: "",
    fullName: "",
    age: "",
    gender: "",
    contact: "",
    address: "",
    sharedCapital: "",
  });

  useEffect(() => {
    if (member) {
      setFormData(member);
    }
  }, [member]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (member) {
        // Update existing member
        const response = await axios.put(`http://localhost:3000/members/${member.memberId}`, formData);
        onSave(response.data);
      } else {
        // Add new member
        const response = await axios.post("http://localhost:3000/members", formData);
        onSave(response.data);
      }
      onClose(); // Close modal after save
    } catch (error) {
      console.error("Error saving member:", error);
    }
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
      <div className="bg-white rounded-lg shadow-lg p-6 w-96">
        <h2 className="text-xl font-bold mb-4">{member ? "Edit Member" : "Add Member"}</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="text"
            name="fullName"
            value={formData.fullName}
            onChange={handleChange}
            placeholder="Full Name"
            required
            className="w-full border border-gray-300 rounded-md p-2"
          />
          <input
            type="number"
            name="age"
            value={formData.age}
            onChange={handleChange}
            placeholder="Age"
            required
            className="w-full border border-gray-300 rounded-md p-2"
          />
          <input
            type="text"
            name="gender"
            value={formData.gender}
            onChange={handleChange}
            placeholder="Gender"
            required
            className="w-full border border-gray-300 rounded-md p-2"
          />
          <input
            type="text"
            name="contact"
            value={formData.contact}
            onChange={handleChange}
            placeholder="Contact Number"
            required
            className="w-full border border-gray-300 rounded-md p-2"
          />
          <input
            type="text"
            name="address"
            value={formData.address}
            onChange={handleChange}
            placeholder="Address"
            required
            className="w-full border border-gray-300 rounded-md p-2"
          />
          <input
            type="number"
            name="sharedCapital"
            value={formData.sharedCapital}
            onChange={handleChange}
            placeholder="Shared Capital"
            required
            className="w-full border border-gray-300 rounded-md p-2"
          />
          <div className="flex justify-between mt-4">
            <button type="submit" className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-700">
              {member ? "Update Member" : "Add Member"}
            </button>
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-700"
            >
              Close
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default MemberModal;
