import React, { useState } from "react";
import axios from 'axios'; // Import axios for making HTTP requests

const MemberModal = ({ onClose, member, onSave }) => {
  const [formData, setFormData] = useState(member || {});
  const [error, setError] = useState(null); // State for error handling
  const [loading, setLoading] = useState(false); // State for loading status

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const isFormValid = () => {
    // Basic validation for required fields
    return (
      formData.fullName &&
      formData.age &&
      formData.contact &&
      formData.address &&
      formData.sharedCapital
    );
  };

  const handleSave = async () => {
    if (!isFormValid()) {
      setError('Please fill out all required fields.');
      return; 
    }

    setError(null); // Reset error state
    setLoading(true); // Set loading state

    const formDataToSend = new FormData();

    // Append all form data to the FormData object
    Object.keys(formData).forEach(key => {
      formDataToSend.append(key, formData[key]);
    });

    // Append file inputs separately
    const fileInputs = [
      "applicationForm",
      "idPicture",
      "barangayClearance",
      "tin",
      "pmes"
    ];
    
    fileInputs.forEach(fileInput => {
      const file = document.getElementById(fileInput)?.files[0];
      if (file) {
        formDataToSend.append(fileInput, file);
      }
    });

    try {
      // Send the request to your backend API
      const response = await axios.post('http://localhost:3001/api/member-information', formDataToSend, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      console.log('Member added:', response.data);
      alert('Member added successfully!'); // Inform user on success
      onSave(formData); // Call the onSave function from parent (optional)
      onClose(); // Close modal
    } catch (error) {
      console.error('Error adding member:', error);
      setError('Error adding member. Please try again.'); // Set error message
    } finally {
      setLoading(false); // Reset loading state
    }
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="fixed inset-0 bg-black opacity-50"></div>
      <div className="bg-white rounded-lg shadow-lg p-6 w-1/2 relative z-10">
        <div className="flex justify-end mb-2">
          <button
            onClick={onClose}
            className="text-red-500 text-2xl font-bold"
          >
            &times;
          </button>
        </div>
        <h1 className="text-2xl font-bold text-gray-800 mb-6">Membership</h1>
        <p className="text-xl font-bold text-gray-800 mb-6">Personal Information</p>

        {error && <p className="text-red-500">{error}</p>} {/* Display error message */}

        <div className="grid grid-cols-2 gap-x-8 gap-y-4">
          <div>
            <label className="block text-gray-700">Full Name:</label>
            <input
              type="text"
              name="fullName"
              value={formData.fullName || ''}
              onChange={handleChange}
              placeholder="Enter full name"
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
              required
            />
          </div>

          <div>
            <label className="block text-gray-700">Age:</label>
            <input
              type="number"
              name="age"
              value={formData.age || ''}
              onChange={handleChange}
              placeholder="Enter age"
              className="mt-1 p-2 border border-gray-300 rounded-md max-w-sm"
              required
            />
          </div>

          <div>
            <label className="block text-gray-700">Contact Number:</label>
            <input
              type="tel"
              name="contact"
              value={formData.contact || ''}
              onChange={handleChange}
              placeholder="Enter contact number"
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
              required
            />
          </div>

          <div>
            <label className="block text-gray-700">Gender:</label>
            <select
              name="gender"
              value={formData.gender || ''}
              onChange={handleChange}
              className="mt-1 p-2 border border-gray-300 rounded-md max-w-sm"
            >
              <option value="">Select Gender</option>
              <option value="male">Male</option>
              <option value="female">Female</option>
            </select>
          </div>

          <div>
            <label className="block text-gray-700">Address:</label>
            <input
              type="text"
              name="address"
              value={formData.address || ''}
              onChange={handleChange}
              placeholder="Enter address"
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
              required
            />
          </div>

          <div>
            <label className="block text-gray-700">Shared Capital:</label>
            <input
              type="number"
              name="sharedCapital"
              value={formData.sharedCapital || ''}
              onChange={handleChange}
              placeholder="Enter Shared capital"
              className="mt-1 p-2 border border-gray-300 rounded-md w-full"
              required
            />
          </div>
        </div>

        <div className="mt-6">
          <h2 className="text-xl font-bold text-gray-800 mb-4">
            Account Information <span className="text-sm font-normal">(Optional)</span>
          </h2>

          <div className="grid grid-cols-2 gap-x-8 gap-y-4">
            <div>
              <label className="block text-gray-700">Email:</label>
              <input
                type="email"
                name="email"
                value={formData.email || ''}
                onChange={handleChange}
                placeholder="Enter email"
                className="mt-1 p-2 border border-gray-300 rounded-md w-full"
              />
            </div>
            <div>
              <label className="block text-gray-700">Password:</label>
              <input
                type="password"
                name="password"
                value={formData.password || ''}
                onChange={handleChange}
                placeholder="Enter password"
                className="mt-1 p-2 border border-gray-300 rounded-md w-full"
              />
            </div>
          </div>
        </div>

        <div className="mt-6">
          <h2 className="text-xl font-bold text-gray-800 mb-4">Required Documents</h2>
          <div className="grid grid-cols-2 gap-x-8 gap-y-4">
            <div>
              <label className="block text-gray-700">Application Form:</label>
              <input type="file" id="applicationForm" name="applicationForm" className="w-full" />
            </div>
            <div>
              <label className="block text-gray-700">ID Picture (1x1 and 2x2):</label>
              <input type="file" id="idPicture" name="idPicture" className="w-full" />
            </div>
            <div>
              <label className="block text-gray-700">Barangay Clearance:</label>
              <input type="file" id="barangayClearance" name="barangayClearance" className="w-full" />
            </div>
            <div>
              <label className="block text-gray-700">TIN:</label>
              <input type="file" id="tin" name="tin" className="w-full" />
            </div>
            <div>
              <label className="block text-gray-700">PMES:</label>
              <input type="file" id="pmes" name="pmes" className="w-full" />
            </div>
          </div>
        </div>

        <div className="mt-6 flex justify-end">
          <button
            onClick={handleSave}
            className={`px-4 py-2 ${loading ? 'bg-gray-400' : 'bg-blue-500'} text-white rounded hover:bg-blue-700 mr-2`}
            disabled={loading} // Disable button while loading
          >
            {loading ? 'Saving...' : 'Save'}
          </button>
          <button
            onClick={onClose}
            className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-700"
          >
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default MemberModal;
