import React, { useState, useEffect } from "react";
import axios from "axios";

const AddUserModal = ({ isOpen, onClose, onSave, userIdToEdit }) => {
  const [newUser, setNewUser] = useState({
    fullName: '',
    age: '',
    gender: '',
    address: '',
    contactNumber: '',
    email: '',
    password: '',
    userType: '',
  });

  const [message, setMessage] = useState('');

  // Fetch user data for editing
  const fetchUserData = async (id) => {
    try {
      const response = await axios.get(`http://localhost:3001/api/user/${id}`);
      if (response.data) {
        setNewUser(response.data);
      } else {
        setMessage("No user found.");
      }
    } catch (error) {
      setMessage("Error fetching user data.");
      console.error(error);
    }
  };

  // Reset form when closing or opening for new user
  const resetForm = () => {
    setNewUser({
      fullName: '',
      age: '',
      gender: '',
      address: '',
      contactNumber: '',
      email: '',
      password: '',
      userType: '',
    });
    setMessage('');
  };

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewUser((prev) => ({ ...prev, [name]: value }));
  };

  // Save user (new or edited)
  const handleSave = async () => {
    try {
      if (userIdToEdit) {
        await axios.put(`http://localhost:3001/api/user/${userIdToEdit}`, newUser);
      } else {
        await axios.post("http://localhost:3001/api/user", newUser);
      }
      onSave(newUser);
      onClose();
    } catch (error) {
      console.error("Error saving user data:", error);
    }
  };

  // Effect to handle editing logic
  useEffect(() => {
    if (isOpen) {
      if (userIdToEdit) {
        fetchUserData(userIdToEdit);
      } else {
        resetForm();
      }
    } else {
      resetForm();
    }
  }, [isOpen, userIdToEdit]);

  // Return early if modal is not open
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50">
      <div className="fixed inset-0 bg-black opacity-50"></div>
      <div className="bg-white rounded-lg shadow-lg p-6 w-1/2 relative z-10">
        <div className="flex justify-end mb-2">
          <button
            onClick={onClose}
            className="text-red-500 text-2xl font-bold"
          >
            &times;
          </button>
        </div>
        <h1 className="text-2xl font-bold text-gray-800 mb-6">User Management</h1>
        <p className="text-xl font-bold text-gray-800 mb-6">User Information</p>
        {message && <p className="text-red-500">{message}</p>}
        <div className="grid grid-cols-2 gap-x-8 gap-y-4">
          {[
            { label: "Full Name", name: "fullName", type: "text" },
            { label: "Age", name: "age", type: "number" },
            { label: "Contact Number", name: "contactNumber", type: "tel" },
            { label: "Gender", name: "gender", type: "select", options: ["male", "female"] },
            { label: "Address", name: "address", type: "text" },
            { label: "User Type", name: "userType", type: "select", options: ["Admin", "User"] },
            { label: "Email", name: "email", type: "email" },
            { label: "Password", name: "password", type: "password" },
          ].map((field) =>
            field.type === "select" ? (
              <div key={field.name}>
                <label className="block text-gray-700">{field.label}:</label>
                <select
                  name={field.name}
                  value={newUser[field.name] || ''}
                  onChange={handleChange}
                  className="mt-1 p-2 border border-gray-300 rounded-md w-full"
                >
                  {field.options.map((option) => (
                    <option key={option} value={option}>
                      {option.charAt(0).toUpperCase() + option.slice(1)}
                    </option>
                  ))}
                </select>
              </div>
            ) : (
              <div key={field.name}>
                <label className="block text-gray-700">{field.label}:</label>
                <input
                  type={field.type}
                  name={field.name}
                  value={newUser[field.name] || ''}
                  onChange={handleChange}
                  placeholder={`Enter ${field.label.toLowerCase()}`}
                  className="mt-1 p-2 border border-gray-300 rounded-md w-full"
                />
              </div>
            )
          )}
        </div>
        <div className="mt-6 flex justify-end">
          <button
            onClick={handleSave}
            className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-700 mr-2"
          >
            Save
          </button>
          <button
            onClick={onClose}
            className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-700"
          >
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default AddUserModal;
