// src/MemberModal.js

import React, { useState } from 'react';
import axios from 'axios';

const MemberModal = ({ isOpen, onClose }) => {
  const [memberName, setMemberName] = useState('');
  const [members, setMembers] = useState([]);
  const [newMember, setNewMember] = useState({
    id: '',
    memberId: '',
    fullName: '',
    age: '',
    contactNumber: '',
    gender: '',
    address: '',
    sharedCapital: '',
    email: '',
    password: ''
  });
  const [message, setMessage] = useState('');

  // Function to search for member information
  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/api/member-information?name=${memberName}`);
      setMembers(response.data);
      setMessage('');
    } catch (error) {
      if (error.response && error.response.status === 404) {
        setMessage('Member not found');
        setMembers([]);
      } else {
        setMessage('Error fetching data');
      }
    }
  };

  // Function to handle adding a new member
  const handleAddMember = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3001/api/member-information', newMember);
      setMessage(response.data.message);
      setNewMember({
        id: '',
        memberId: '',
        fullName: '',
        age: '',
        contactNumber: '',
        gender: '',
        address: '',
        sharedCapital: '',
        email: '',
        password: ''
      });
    } catch (error) {
      setMessage('Error adding member');
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button onClick={onClose}>Close</button>
        <h1>Member Management</h1>
        
        <h2>Search Member</h2>
        <input 
          type="text" 
          value={memberName} 
          onChange={(e) => setMemberName(e.target.value)} 
          placeholder="Enter member name" 
        />
        <button onClick={handleSearch}>Search</button>
        {message && <p>{message}</p>}
        {members.length > 0 && (
          <div>
            <h3>Member Details</h3>
            <ul>
              {members.map((member) => (
                <li key={member.id}>
                  {member.fullName} - {member.contactNumber}
                </li>
              ))}
            </ul>
          </div>
        )}

        <h2>Add New Member</h2>
        <form onSubmit={handleAddMember}>
          {Object.keys(newMember).map((key) => (
            <div key={key}>
              <label>
                {key.charAt(0).toUpperCase() + key.slice(1)}:
                <input 
                  type="text" 
                  value={newMember[key]} 
                  onChange={(e) => setNewMember({ ...newMember, [key]: e.target.value })} 
                  required 
                />
              </label>
            </div>
          ))}
          <button type="submit">Add Member</button>
        </form>
      </div>
    </div>
  );
};

export default MemberModal;
