import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('System admin');
  const [errorMessage, setErrorMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErrorMessage('');

    try {
      const response = await axios.post('http://localhost:3001/api/auth/login', {
        username,
        password,
        userType,
      });

      const { token, user } = response.data;

      // Store token securely (use HTTP-only cookies for better security in production)
      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(user));

      alert('Login successful!');
      navigate('/dashboard');
    } catch (error) {
      setErrorMessage(error.response?.data?.message || 'Something went wrong');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="flex flex-col items-center p-6 shadow-md bg-white max-w-2xl w-full rounded-md">
        <form onSubmit={handleSubmit} className="w-full">
          <h2 className="text-2xl mb-4">Log In</h2>
          {errorMessage && <p className="text-red-500">{errorMessage}</p>}
          <label>User Type</label>
          <select value={userType} onChange={(e) => setUserType(e.target.value)} required>
            <option value="System admin">System admin</option>
            <option value="Admin">Admin</option>
            <option value="Loan officer">Loan officer</option>
            <option value="Teller">Teller</option>
          </select>
          <label>Username</label>
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} required />
          <label>Password</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          <button type="submit" disabled={loading}>
            {loading ? 'Logging in...' : 'Log In'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
