import React, { useState, useEffect } from "react";
import axios from "axios";
import { FaPlus, FaTrash, FaEdit, FaSearch } from "react-icons/fa";
import AddUserModal from "../modal/AddMemberModal.js";

const Users = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState("");
  const [isAddEditModalOpen, setIsAddEditModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");

  const fetchUsers = async () => {
    setLoading(true);
    setError(null);
    setSuccessMessage("");
    try {
      const params = searchTerm ? { name: searchTerm } : {};
      const response = await axios.get("http://localhost:3001/api/users", { params });
      setUsers(response.data);
    } catch (err) {
      setError(err.response?.data?.message || "Failed to fetch users.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const debounce = setTimeout(fetchUsers, 500); // Debounce for API calls
    return () => clearTimeout(debounce);
  }, [searchTerm]);

  const openAddEditModal = (user) => {
    setSelectedUser(user || null);
    setIsAddEditModalOpen(true);
  };

  const closeModals = () => {
    setIsAddEditModalOpen(false);
    setSelectedUser(null);
  };

  const handleSave = async (updatedUser) => {
    try {
      if (updatedUser._id) {
        // Update user
        await axios.put(`http://localhost:3001/api/users/${updatedUser._id}`, updatedUser);
      } else {
        // Add new user
        await axios.post("http://localhost:3001/api/users", updatedUser);
      }
      setSuccessMessage("User saved successfully!");
      fetchUsers();
    } catch (err) {
      setError(err.response?.data?.message || "Failed to save user.");
    } finally {
      closeModals();
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this user?")) {
      try {
        await axios.delete(`http://localhost:3001/api/users/${id}`);
        setUsers((prevUsers) => prevUsers.filter((user) => user._id !== id));
        setSuccessMessage("User deleted successfully!");
      } catch (err) {
        setError(err.response?.data?.message || "Failed to delete user.");
      }
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-4">User List</h2>
      {successMessage && <div className="text-green-500">{successMessage}</div>}
      {error && <div className="text-red-500">{error}</div>}
      <div className="mb-4 flex justify-end">
        <div className="relative w-80 mr-4">
          <input
            type="text"
            placeholder="Search user..."
            className="px-10 py-2 border border-gray-300 rounded-md w-full"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <FaSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
        </div>
        <button
          className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-700 flex items-center space-x-2"
          onClick={() => openAddEditModal(null)}
        >
          <FaPlus />
          <span>Add User</span>
        </button>
      </div>
      {loading ? (
        <div>Loading...</div>
      ) : (
        <table className="min-w-full bg-white border border-gray-300">
          <thead>
            <tr className="bg-green-200">
              <th className="py-2 px-4 border-b border-gray-300">Full Name</th>
              <th className="py-2 px-4 border-b border-gray-300">Gender</th>
              <th className="py-2 px-4 border-b border-gray-300">Address</th>
              <th className="py-2 px-4 border-b border-gray-300">Contact</th>
              <th className="py-2 px-4 border-b border-gray-300">Email</th>
              <th className="py-2 px-4 border-b border-gray-300">User Type</th>
              <th className="py-2 px-4 border-b border-gray-300">Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.length > 0 ? (
              users.map((user) => (
                <tr key={user._id} className="text-center">
                  <td className="py-2 px-4 border-b border-gray-300">{user.fullName}</td>
                  <td className="py-2 px-4 border-b border-gray-300">{user.gender}</td>
                  <td className="py-2 px-4 border-b border-gray-300">{user.address}</td>
                  <td className="py-2 px-4 border-b border-gray-300">{user.contact}</td>
                  <td className="py-2 px-4 border-b border-gray-300">{user.email}</td>
                  <td className="py-2 px-4 border-b border-gray-300">{user.userType}</td>
                  <td className="py-2 px-4 border-b border-gray-300">
                    <div className="flex justify-center">
                      <button
                        className="bg-orange-500 text-white px-4 py-2 rounded mr-2"
                        onClick={() => openAddEditModal(user)}
                      >
                        <FaEdit />
                      </button>
                      <button
                        className="bg-red-500 text-white px-4 py-2 rounded"
                        onClick={() => handleDelete(user._id)}
                      >
                        <FaTrash />
                      </button>
                    </div>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="7" className="py-4 text-center">
                  No users found
                </td>
              </tr>
            )}
          </tbody>
        </table>
      )}
      {isAddEditModalOpen && (
        <AddUserModal member={selectedUser} onClose={closeModals} onSave={handleSave} />
      )}
    </div>
  );
};

export default Users;
