import React, { useState } from "react";
import axios from "axios";
import pic from "../blankPicture.png"; // Fallback placeholder image

const MembershipInformationModal = ({ member, onClose }) => {
    if (!member) return null;

    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const memberSinceDate = new Date(member.registrationDate);
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    const formattedDate = memberSinceDate.toLocaleDateString('en-US', options);

    const dateOfBirth = new Date(member.dateOfBirth);
    const options2 = { year: 'numeric', month: 'long', day: 'numeric' };
    const formDate = dateOfBirth.toLocaleDateString('en-US', options2);

    const idPictureUrl = member.idPicture ? `http://localhost:3001/uploads/${member.idPicture}` : pic;

    return (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-75 flex justify-center items-center z-50">
            <div className="bg-white p-8 rounded-lg shadow-lg max-w-7xl">
                <div className="flex justify-end">
                    <button onClick={onClose} className="text-red-500 text-2xl font-bold">
                        &times;
                    </button>
                </div>
                <div className="flex items-start space-x-10">
                    <div className="text-center">
                        <img
                            src={idPictureUrl}  
                            alt="ID Picture"
                            className="w-32 h-32 rounded-full object-cover mx-auto"
                        />
                        <h5 className="text-sm mt-2"> {member.memberType}</h5>
                        <h3 className="text-3xl font-bold mt-1"> {member.fullNameLastName + ', ' + member.fullNameFirstName + ', ' + member.fullNameMiddleName}</h3>
                    </div>
                    <div className="flex-1">
                        <h2 className="text-2xl font-bold mb-4">Member Information</h2>
                        <div className="grid grid-cols-3">
                            <div className="mr-4">
                                <p className="text-gray-700">
                                    <span className="font-bold">Membership ID:</span> {member.memberCode}
                                </p>
                                <p className="text-gray-700">
                                    <span className="font-bold">Member since:</span> {formattedDate}
                                </p>
                                <p className="text-gray-700">
                                    <span className="font-bold">Age:</span> {member.age}
                                </p>
                                <p className="text-gray-700">
                                    <span className="font-bold">Sex:</span> {member.sex}
                                </p>
                                <p className="text-gray-700">
                                    <span className="font-bold">Contact No:</span> {member.contactNumber}
                                </p>

                            </div>
                            <div>
                            <p className="text-gray-700">
                                    <span className="font-bold">TIN:</span> {member.tinNumber}
                                </p>
                                <p className="text-gray-700">
                                <span className="font-bold">Address:</span> {member.houseNoStreet + ', ' + member.barangay + ', ' + member.city}
                                </p>
                                <p className="text-gray-700">
                                    <span className="font-bold">Date of birth:</span> {formDate}
                                </p>
                                <p className="text-gray-700">
                                    <span className="font-bold">Place of birth:</span> {member.birthplaceProvince}
                                  
                                </p>
                                <p className="text-gray-700">
                                    <span className="font-bold">Civil status:</span> {member.civilStatus}
                                </p>
                            </div>
                            <div>
                                <p className="text-gray-700">
                                    <span className="font-bold">Highest Education:</span> {member.highestEducationalAttainment}
                                </p>
                                <p className="text-gray-700">
                                    <span className="font-bold">Occupation Source Of Income:</span> {member.occupationSourceOfIncome}
                                </p>
                                <p className="text-gray-700">
                                    <span className="font-bold">Spouse Name:</span> {member.spouseName}
                                </p>
                                <p className="text-gray-700">
                                    <span className="font-bold">Occupation Source Of Income:</span> {member.spouseOccupationSourceOfIncome}
                                </p>
                            </div>
                        </div>
                    </div>
                </div>

                <hr className="border-t border-solid border-gray-400 my-2" />
                <div className="mt-6">
                    <div className="grid grid-cols-2 gap-4">
                        <div>
                        <h3 className="text-2xl font-bold mb-2">Beneficiaries</h3>
                            <p className="text-gray-700">
                                <span className="font-bold">Beneficiary name:</span> {member.beneficiaryName}
                             </p>
                             <p className="text-gray-700">
                                <span className="font-bold">Relationship:</span> {member.relationship}
                             </p>
                             <p className="text-gray-700">
                                <span className="font-bold">Contact Number:</span> {member.beneficiaryContactNumber}
                             </p>
                        </div>

                        <div>    
                        <h3 className="text-2xl font-bold mb-2">Character reference</h3>
     
                            <p className="text-gray-700">
                                <span className="font-bold">Beneficiary name:</span> {member.referenceName}
                             </p>
                             <p className="text-gray-700">
                                <span className="font-bold">Relationship:</span> {member.position}
                             </p>
                             <p className="text-gray-700">
                                <span className="font-bold">Contact Number:</span> {member.referenceContactNumber}
                             </p>
                        </div>
                    </div>
                </div>

                <hr className="border-t border-solid border-gray-400 my-4" />
                <div className="mt-8">
                    <h3 className="text-2xl font-bold mb-4">Account Information</h3>
                    <div className="grid grid-cols-2 gap-4">
                        <div>
                        <p className="font-bold">Email:</p>
                        <p>{member.email}</p>
                        </div>
                        <div>
                        <p className="font-bold">Password:</p>
                        <p>{member.password}</p>
                        </div>
                        <div>
                        <p className="font-bold">Account status:</p>
                        <p>{member.accountStatus}</p>
                        </div>
                    </div>

                    {/* Display success or error messages */}
                    {successMessage && <div className="text-green-500 mt-4">{successMessage}</div>}
                    {errorMessage && <div className="text-red-500 mt-4">{errorMessage}</div>}

                    {member.accountStatus !== 'Active' && (
                        <button
                            onClick={async () => {
                                try {
                                    // Use member.memberId for the API request
                                    await axios.put(`http://localhost:3001/api/members/activate/${member.memberId}`);
                                    
                                    // Update the member state with new values
                                    setMember((prevMember) => ({
                                        ...prevMember,
                                        accountStatus: 'Active',
                                        email: member.memberCode,  
                                        password: member.memberCode 
                                    }));

                                    // Show success message
                                    setSuccessMessage("Account activated successfully!");
                                    setErrorMessage('');  // Clear error message
                                } catch (error) {
                                    console.error("Error activating account:", error);
                                    setErrorMessage("Error activating account.");
                                    setSuccessMessage('');  // Clear success message
                                }
                            }}
                            className="mt-4 px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
                        >
                            Activate Account
                        </button>
                    )}
                </div>
            </div>
        </div>
    );
};

export default MembershipInformationModal;
