import React, { useState, useEffect } from "react";
import axios from "axios";

const MemberModal = ({ member, onClose, onSave }) => {
  const [formData, setFormData] = useState({
    memberId: '',
    fullName: '',
    age: '',
    gender: '',
    contact: '',
    address: '',
    sharedCapital: '',
  });

  // Effect to set form data when editing a member
  useEffect(() => {
    if (member) {
      setFormData(member);
    }
  }, [member]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (member) {
        // Update existing member
        await axios.put(`http://localhost:3000/api/member/${member.memberId}`, formData);
      } else {
        // Create new member
        await axios.post('http://localhost:3000/api/member', formData);
      }
      onSave(formData); // Pass the saved member back to the parent
    } catch (error) {
      console.error("Error saving member data:", error);
      alert("Error saving member data");
    }
  };

  return (
    <div className="modal">
      <h2>{member ? 'Edit Member' : 'Add Member'}</h2>
      <form onSubmit={handleSubmit}>
        {/* Replace with appropriate form fields */}
        <input type="text" name="fullName" placeholder="Full Name" value={formData.fullName} onChange={handleChange} required />
        <input type="number" name="age" placeholder="Age" value={formData.age} onChange={handleChange} required />
        <input type="text" name="gender" placeholder="Gender" value={formData.gender} onChange={handleChange} required />
        <input type="text" name="contact" placeholder="Contact Number" value={formData.contact} onChange={handleChange} required />
        <input type="text" name="address" placeholder="Address" value={formData.address} onChange={handleChange} required />
        <input type="number" name="sharedCapital" placeholder="Shared Capital" value={formData.sharedCapital} onChange={handleChange} required />
        <button type="submit">{member ? 'Update' : 'Add'} Member</button>
        <button type="button" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
};

export default MemberModal;
